INSERT INTO `lp_question` VALUES ('4', '4', '00010001', '0', '判断题', '《消费者权益保护法》规定，消费者在购买、使用商品和接受服务时享有人身安全不受损害的权利，不包括财产不受损害的权利。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('5', '5', '00010001', '0', '判断题', '《消费者权益保护法》规定，经营者提供的机动车、计算机、电视机、电冰箱、空调器、洗衣机等耐用商品或者装饰装修等服务，消费者自接受商品或者服务之日起六个月内发现瑕疵，发生争议的，由经营者承担有关瑕疵的举证责任。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('6', '6', '00010001', '0', '判断题', '《消费者权益保护法》规定，经营者提供的商品或者服务不符合质量要求的，消费者可以依照国家规定、当事人约定退货，或者要求经营者履行更换、修理等义务。没有国家规定和当事人约定的，消费者可以自收到商品之日起七日内退货。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('7', '7', '00010001', '0', '判断题', '《消费者权益保护法》规定，消费者向有关行政部门投诉的，该部门应当自收到投诉之日起七日内，予以处理并告知消费者。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('8', '8', '00010001', '0', '判断题', '《消费者权益保护法》规定，经营者提供商品或者服务有欺诈行为的，应当按照消费者的要求增加赔偿其受到的损失，增加赔偿的金额为消费者购买商品的价款或者接受服务的费用的三倍；增加赔偿的金额不足五百，为五百。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('9', '9', '00010001', '0', '判断题', '《消费者权益保护法》的保护对象为生活消费需要购买、使用商品或者接受服务的个人、单位或集体。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('10', '10', '00010001', '0', '判断题', '2013年修正的《中华人民共和国消费者权益保护法》明确规定，网购商品一律实行7天无理由退货。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('11', '11', '00010001', '0', '判断题', '保护消费者的合法权益是全社会的共同责任。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('12', '12', '00010001', '0', '判断题', '有关行政部门应当听取消费者和消费者协会等组织对经营者交易行为、商品和服务质量问题的意见，及时调查处理，保护消费者的合法权益。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('13', '13', '00010001', '0', '判断题', '安全生产管理，坚持安全第一、预防为主的方针。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('14', '14', '00010001', '0', '判断题', '生产经营单位进行爆破、吊装等危险作业，应当安排专门人员进行现场安全管理,要确保操作规程的遵守和安全措施的落实。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('15', '15', '00010001', '0', '判断题', '生产经营单位发生生产安全事故造成人员伤亡、他人财产损失的，应当依法承担赔偿责任；拒不承担或者其负责人逃匿的，由公安部门依法强制执行。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('16', '16', '00010001', '0', '判断题', '生产经营单位的从业人员有依法获得安全生产保障的权利，并应当依法履行安全生产方面的义务。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('17', '17', '00010001', '0', '判断题', '生产经营单位必须为从业人员提供符合国家标准或者行业标准的劳动防护用品，并监督、教育从业人员按照使用规则佩戴、使用。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('18', '18', '00010001', '0', '判断题', '生产经营单位的法定代表人对本单位的安全生产工作全面负责。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('19', '19', '00010001', '0', '判断题', '危险物品的生产、储存单位以及矿山、金属冶炼单位应当有注册安全工程师从事安全生产管理工作。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('20', '20', '00010001', '0', '判断题', '各级人民政府及其有关部门应当采取多种形式，加强对有关安全生产的法律、法规和安全生产知识的宣传，增强全社会的安全生产意识。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('21', '21', '00010001', '0', '判断题', '生产经营单位应当具备的安全生产条件所必需的资金投入，由生产经营单位的决策机构，主要负责人或者个人经营的投资人予以保证，并对由于安全生产所必需的资金投入不足导致的后果承担责任。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('22', '22', '00010001', '0', '判断题', '生产经营单位生产、经营、运输、储存、使用危险物品或者处置废弃危险物品，必须执行有关法律、法规和国家标准或者行业标准，建立专门的安全管理制度，采取可靠的安全措施，接受有关主管部门依法实施的监督管理。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('23', '23', '00010001', '0', '判断题', '两个以上生产经营单位在同一作业区域内进行生产经营活动，可能危及对方生产安全的，应当签订安全生产管理协议，明确各自的安全生产管理职责和应当采取的安全措施，并指定专职安全生产管理人员进行安全检查与协调。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('24', '24', '00010001', '0', '判断题', '生产经营单位应当建立健全生产安全事故隐患排查治理制度，采取技术、管理措施，及时发现并消除事故隐患。事故隐患排查治理情况应当如实记录，并向从业人员通报。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('25', '25', '00010001', '0', '判断题', '党和国家工作人员或者其他从事公务的人员中的共产党员，利用职务上的便利，为他人谋利益，接受其邀请，本人或携带亲友外出旅游，费用由邀请方支付的行为，以贪污论。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('26', '26', '00010001', '0', '判断题', '国家工作人员利用职务上的便利为请托人谋取利益之前或者之后，约定在其离职后收受请托人财物，并在离职后收受的，不能以受贿论处。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('27', '27', '00010001', '0', '判断题', '行政监察机关的监督是行政机关的一种内部监督。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('28', '28', '00010001', '0', '判断题', '对行政监察对象的监察,重点是对其行政行为的监察。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('29', '29', '00010001', '0', '判断题', '监察机关对受理的不服主管行政机关行政处分决定的申诉，经复查认为原决定不适当的，可以建议原决定机关予以变更或者撤销；监察机关在职权范围内，也可以直接作出变更或者撤销的决定。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('30', '30', '00010001', '0', '判断题', '《中国共产党纪律处分条例》规定，预备党员违犯党纪，应当对其批评教育或者延长预备期。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('31', '31', '00010001', '0', '判断题', '《中国共产党纪律处分条例》规定，党的工作人员，是指党的各级机关中的工作人员和党的基层组织中专职、兼职从事党内事务的党员。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('32', '32', '00010001', '0', '判断题', '《中国共产党纪律处分条例》规定，利用职务上的便利操办婚丧喜庆事宜，在社会上造成不良影响，情节严重的，给予撤销党内职务处分。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('33', '33', '00010001', '0', '判断题', '《中国共产党纪律处分条例》规定，在国内公务活动或者对外交往中接受礼品，按照规定应当交公而不交公的，以受贿论处。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('34', '34', '00010001', '0', '判断题', '《中国共产党纪律处分条例》规定，违反国有资产管理规定，造成国有资产流失的，对主要责任者和其他直接责任人员，情节较重的，给予警告或者严重警告处分。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('35', '35', '00010001', '0', '判断题', '《中国共产党纪律处分条例》规定，利用职务上的便利，将本人或者亲属应当由个人支付的费用，由下属单位或者其他单位支付、报销的行为属贪污行为。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('36', '36', '00010001', '0', '判断题', '《中国共产党纪律处分条例》规定，党和国家机关违反有关规定，在对内对外活动中将接受的礼品集体私分的，以私分国有资产论，根据个人所得数额和所起作用，以违反财经纪律行为处理。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('37', '37', '00010001', '0', '判断题', '共产党员永远是劳动人民的普通一员，除了法律和政策规定范围内的个人利益和工作职权以外，所有共产党员都不得谋求任何私利和特权。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('38', '38', '00010001', '0', '判断题', '公务员在定期考核中被确定为不称职的，按照规定程序降低一个职务层次任职。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('39', '39', '00010001', '0', '判断题', '选任制公务员在选举结果生效时即任当选职务；任期届满不再连任，或者任期内辞职、被罢免、被撤职的，其所任职务即终止。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('40', '40', '00010001', '0', '判断题', '公务员因工作需要在机关外兼职，应当经有关机关批准，可以领取兼职报酬。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('41', '41', '00010001', '0', '判断题', '厅局级正职以下领导职务或者副调研员以上及其他相当职务层次的非领导职务出现空缺，可以面向社会公开选拔，产生任职人选。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('42', '42', '00010001', '0', '判断题', '国家对公务员实行分类管理，提高管理效能和科学化水平。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('43', '43', '00010001', '0', '判断题', '公务员晋升领导职务的，应当按照有关规定实行任职前公示制度和任职试用期制度。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('44', '44', '00010001', '0', '判断题', '给予行政机关公务员处分，可以与其违法违纪行为的性质、情节、危害程度相适应。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('45', '45', '00010001', '0', '判断题', '弄虚作假，误导、欺骗领导和公众，造成不良后果的，给予警告、记过或者记大过处分；情节较重的，给予降级处分；情节严重的，给予开除处分。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('46', '46', '00010001', '0', '判断题', '监察机关依法行使职权，不受其他行政部门、社会团体和个人的干涉。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('47', '47', '00010001', '0', '判断题', '给予行政机关公务员处分，应当自批准立案之日起6个月内作出决定；案情复杂或者遇有其他特殊情形的，办案期限可以延长，但是最长不得超过12个月。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('48', '48', '00010001', '0', '判断题', '行政机关公务员违法违纪，已经被立案调查，不宜继续履行职责的，任免机关可以决定暂停其履行职务。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('49', '49', '00010001', '0', '判断题', '国家机关工作人员和法律、行政法规规定的禁止在公司中兼职的其他人员，可以在证券公司中兼任职务。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('50', '50', '00010001', '0', '判断题', '公务员解除处分后，晋升工资档次、级别和职务不再受原处分的影响。但是，解除降级、撤职处分的，不视为恢复原级别、原职务。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('51', '51', '00010001', '0', '判断题', '《公务员法》规定，定期考核的结果作为调整公务员职务、级别、工资以及公务员奖励、培训、辞退的依据。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('52', '52', '00010001', '0', '判断题', '《各级人民代表大会常务委员会监督法》规定，各级人民代表大会常务委员会对本级人民政府、人民法院和人民检察院的工作实施监督，促进依法行政、公正司法。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('53', '53', '00010001', '0', '判断题', '2015政府工作报告指出，经济发展进入新常态，精神面貌要有新状态。广大公务员特别是领导干部要始终把为人民谋发展增福祉作为最大责任，始终把现代化建设使命扛在肩上，始终把群众冷暖忧乐放在心头。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('54', '54', '00010001', '0', '判断题', '我国是世界上最大的发展中国家，仍处于并将长期处于社会主义初级阶段，发展是硬道理，是解决一切问题的基础和关键。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('55', '55', '00010001', '0', '判断题', '2014年8月31日第十二届全国人民代表大会常务委员会第十次会议通过：从2017年开始，香港特别行政区行政长官选举可以实行由等额选举产生的办法。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('56', '56', '00010001', '0', '判断题', '社会主义法治理念具有丰富而深刻的内涵，其中属于社会主义法治本质要求的是执法为民。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('57', '57', '00010001', '0', '判断题', '领导干部在线索核查、立案、侦查、审查起诉、审判、执行等环节为案件当事人请托说情的，属于违法干预司法活动，党委政法委按程序报经批准后予以通报。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('58', '58', '00010001', '0', '判断题', '习主席指出，要把维护社会大局稳定作为基本任务，把促进社会公平正义作为核心价值追求，把保障人民安居乐业作为根本目标。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('59', '59', '00010001', '0', '判断题', '实施依法治国基本方略，必须坚持法治国家、法治政府、法治社会一体建设。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('60', '60', '00010001', '0', '判断题', '社会主义核心价值体系是兴国之魂，决定着中国特色社会主义发展方向。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('61', '61', '00010001', '0', '判断题', '为保障我国社会主义市场经济的健康发展，我国社会主义道德建设的重点应该是诚实守信。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('62', '62', '00010001', '0', '判断题', '党领导人民制定宪法和法律，党必须在宪法和法律范围内活动。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('63', '63', '00010001', '0', '判断题', '依法治国是实现国家长治久安的重要保障。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('64', '64', '00010001', '0', '判断题', '当前，党领导人民治理国家的基本方略是依法治国。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('65', '65', '00010001', '0', '判断题', '工作作风上的问题上，作风是否确实好转，要以人民满意为标准。要广泛听取群众意见和建议，自觉接受群众评议和社会监督。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('66', '66', '00010001', '0', '判断题', '必须切实转变政府职能，深化行政体制改革，创新行政管理方式，增强政府公信力和执行力，建设法治政府和服务型政府。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('67', '67', '00010001', '0', '判断题', '十八届三中全会强调，全面深化改革，必须高举中国特色社会主义伟大旗帜，以马克思列宁主义、毛泽东思想、邓小平理论、“三个代表”重要思想、科学发展观为指导，坚定信心，凝聚共识，统筹谋划，协同推进，坚持社会主义市场经济改革方向，以促进社会公平正义、增进人民福祉为出发点和落脚点。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('68', '68', '00010001', '0', '判断题', '十八届三中全会指出，全面深化改革的总目标是完善和发展中国特色社会主义制度，推进国家治理体系、治理能力现代化。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('69', '69', '00010001', '0', '判断题', '经济体制改革是全面深化改革的重点，核心问题是处理好政府和市场的关系。', '', '', null, '', '0', '0');
