INSERT INTO `lp_question` VALUES ('71', '71', '00010001', '0', '判断题', '建设社会主义文化强国，增强国家文化软实力，必须坚持社会主义先进文化前进方向，坚持中国特色社会主义文化发展道路，坚持以人民为中心的工作导向。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('72', '72', '00010001', '0', '判断题', '十八届四中全会向国内外鲜明宣示，我们将坚定不移走中国特色社会主义法治道路，以建设中国特色社会主义法治体系为总抓手，在法治轨道上推进国家治理体系和治理能力现代化，为建设法治中国而奋斗。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('73', '73', '00010001', '0', '判断题', '在当代中国，坚持发展是硬道理的本质要求就是坚持科学发展。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('74', '74', '00010001', '0', '判断题', '党的十八大报告指出:推进经济结构战略性调整,这是加快转变经济发展方式的主攻方向。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('75', '75', '00010001', '0', '判断题', '十八届三中全会提出，科学的宏观调控，有效的政府治理，是发挥社会主义市场经济体制优势的内在要求。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('76', '76', '00010001', '0', '判断题', '党的十八大按照中国特色社会主义事业五位一体总体布局对党和国家事业进行了全面部署。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('77', '77', '00010001', '0', '判断题', '我国过去30多年的快速发展靠的是改革开放，我国未来发展也必须坚定不移依靠改革开放。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('78', '78', '00010001', '0', '判断题', '《中共中央关于全面深化改革若干重大问题的决定》指出，必须更加注重改革的系统性、整体性、协同性，加快发展社会主义市场经济、民主政治、先进文化、和谐社会、生态文明。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('79', '79', '00010001', '0', '判断题', '十八届三中全会提出，公有制为主体、多种所有制经济共同发展的基本经济制度，是中国特色社会主义制度的重要支柱，也是社会主义市场经济体制的根基。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('80', '80', '00010001', '0', '判断题', '十八届三中全会指出，财政是国家治理的基础和重要支柱，科学的财税体制是优化资源配置、维护市场统一、促进社会公平、实现国家长治久安的制度保障。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('81', '81', '00010001', '0', '判断题', '十八届三中全会一致认为面对十分复杂的国际形式和艰巨繁重的国内改革发展稳定任务，中央政治局全面贯彻党的十八大精神，高举中国特色社会主义伟大旗帜，以邓小平理论、“三个代表”重要思想、科学发展观为指导，团结带领全党全军全国各族人民，着力稳增长、调结构、促改革。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('82', '82', '00010001', '0', '判断题', '《中共中央关于全面深化改革若干重大问题的决定》提出，优化资源配置、维护市场统一、促进社会公平、实现国家长治久安的制度保障是科学的财税体制。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('83', '83', '00010001', '0', '判断题', '《中共中央关于全面深化改革若干重大问题的决定》提出，改革开放是党在新时代条件下带领全国各族人民进行的新的伟大革命，是当代中国最鲜明的特色，是决定当代中国命运的关键抉择，是党和人民事业大踏步赶上时代的重要法宝。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('84', '84', '00010001', '0', '判断题', '《中共中央关于全面深化改革若干重大问题的决定》指出，要紧紧围绕使市场在资源配置中起决定性作用深化经济体制改革，坚持和完善基本经济制度，加快完善现代市场体系、宏观调控体系、开放型经济体系。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('85', '85', '00010001', '0', '判断题', '《中共中央关于全面深化改革若干重大问题的决定》指出，必须深化政治体制改革，加快推进社会主义民主政治制度化、规范化、程序化，建设社会主义法制国家，发展更加广泛、更加充分、更加健全的人民民主。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('86', '86', '00010001', '0', '判断题', '《中共中央关于全面深化改革若干重大问题的决定》指出，紧紧围绕更好保障和改善民生、促进社会公平正义深化社会体制改革，改革收入分配制度，促进共同富裕，推进社会领域制度创新，推进基本公共服务均等化，加快形成科学有效的社会治理体制，确保社会既充满活力又和谐有序。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('87', '87', '00010001', '0', '判断题', '《中共中央关于全面深化改革若干重大问题的决定》指出，全面深化改革，必须立足于我国东中西部经济发展不平衡这个最大实际，坚持发展仍是解决我国所有问题的关键这个重大战略判断，以经济建设为中心，发挥经济体制改革牵引作用，推动生产关系同生产力、上层建筑同经济基础相适应，推动经济社会持续健康发展。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('88', '88', '00010001', '0', '判断题', '《中共中央关于全面深化改革若干重大问题的决定》强调，改革开放的成功实践为全面深化改革提供了重要的经验，必须长期坚持。最重要的是，坚持党的领导，贯彻党的基本路线，不走封闭僵化的老路，不走改旗易帜的邪路，坚定走中国特色社会主义道路，始终确保改革正确方向。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('89', '89', '00010001', '0', '判断题', '《中共中央关于全面深化改革若干重大问题的决定》指出，要建立公平开放透明的市场规则，完善主要由市场决定价格的机制，建立城乡统一的建设用地市场，完善金融市场体系，深化科技体制改革。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('90', '90', '00010001', '0', '判断题', '十八届三中全会提出，发展社会主义民主政治，必须以保证人民当家作主为根本，坚持和完善人民代表大会制度、中国共产党领导的多党合作和政治协商制度、民族区域自治制度以及基层群众自制制度，更加注重健全民主制度、丰富民主形式，充分发挥我国社会主义政治制度优越性。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('91', '91', '00010001', '0', '判断题', '十八届三中全会提出，实现发展成果更多更公平惠及全体人民，必须加快社会事业改革，解决好人民最关心最直接最现实的利益问题，更好满足人民需求。要深化教育领域综合改革，健全促进就业创业体制机制，形成合理有序的收入分配格局，建立更加公平可持续的社会保障制度，深化医药卫生体制改革。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('92', '92', '00010001', '0', '判断题', '十八届三中全会号召，全党同志要紧密团结在以习近平同志为总书记的党中央周围，锐意进取，攻坚克难，谱写改革开放伟大事业历史新篇章，为全面建成小康社会、不断夺取中国特色社会主义新胜利、实现中华民族伟大复兴的中国梦而奋斗。', '', '', null, '', '0', '0');
