INSERT INTO `lp_question` VALUES ('96', '96', '00010001', '0', '判断题', '十八届四中全会指出，优化司法职权配置，推动实行审判权和执行权相分离的体制改革试点，最高人民法院设立巡回法庭，探索设立跨行政区划的人民法院和人民检察院，探索建立检察机关提起公益诉讼制度。', '', '', null, '', '0', '0');
