INSERT INTO `lp_question` VALUES ('101', '101', '00010001', '0', '判断题', '股票发行价格可以按票面金额，也可以低于或超过票面金额。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('102', '102', '00010001', '0', '判断题', '有限责任公司的股东以其认缴的出资额为限对公司承担责任；股份有限公司的股东以其全部财产为限对公司承担责任。', '', '', null, '', '0', '0');
