INSERT INTO `lp_question` VALUES ('104', '104', '00010001', '0', '判断题', '经公证的民事法律行为、有法律意义的事实和文书，人民法院应当作为认定事实的根据，但有相反证据足以推翻该项公证的除外。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('105', '105', '00010001', '0', '判断题', '居住在香港、澳门、台湾地区的当事人，委托他人代理申办涉及继承、财产权益处分、人身关系变更等重要公证事项的，其授权委托书应当经其居住地的公证人(机构)公证，或者经司法部指定的机构、人员证明。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('106', '106', '00010001', '0', '判断题', '公证机构和律师事务所都应当在核定的执业区域内受理业务。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('107', '107', '00010001', '0', '判断题', '公证事项的利害关系人认为公证书有错误的，应当自公证书出具之日起一年内向出具该公证书的公证机构提出复查。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('108', '108', '00010001', '0', '判断题', '《政府采购法》规定，询价会议是政府采购采用的主要方式。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('109', '109', '00010001', '0', '判断题', '《政府采购法》规定，货物或者服务项目采取邀请招标方式采购的，采购人应当从符合相应资格条件的供应商中，通过随机方式选择四家以上的供应商，并向其发出投标邀请书。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('110', '110', '00010001', '0', '判断题', '《政府采购法》规定，谈判小组由采购人的代表和有关专家共三人以上的单数组成，其中专家的人数不得少于成员总数的三分之二。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('111', '111', '00010001', '0', '判断题', '《政府采购法》规定，采购人与中标、成交供应商应当在中标、成交通知书发出之日起三十日内，按照采购文件确定的事项签订政府采购合同。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('112', '112', '00010001', '0', '判断题', '《政府采购法》规定，政府采购项目的采购合同自签订之日起七个工作日内，采购人应当将合同副本报同级政府采购监督管理部门和有关部门备案。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('113', '113', '00010001', '0', '判断题', '政府采购实行集中采购和分散采购相结合。集中采购的范围由地级市以上人民政府公布的集中采购目录确定。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('114', '114', '00010001', '0', '判断题', '《政府采购法》规定，任何单位和个人不得采用任何方式，阻挠和限制供应商自由进入本地区和本行业的政府采购市场。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('115', '115', '00010001', '0', '判断题', '《政府采购法》规定，政府采购合同履行中，采购人需追加与合同标的相同的货物、工程或者服务的，在不改变合同其他条款的前提下，可以与供应商协商签订补充合同，但所有补充合同的采购金额不得超过原合同采购金额的百分之十五。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('116', '116', '00010001', '0', '判断题', '《政府采购法》规定，供应商认为采购文件、采购过程和中标、成交结果使自己的权益受到损害的，可以在知道或者应知其权益受到损害之日起七个工作日内，以书面形式向采购人提出质疑。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('117', '117', '00010001', '0', '判断题', '国家机关工作人员在执行职务中，侵犯公民、法人的合法权益造成损害的，应当由国家机关承担民事责任。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('118', '118', '00010001', '0', '判断题', '若发现已生效的行政执法行为违法或不当，则原行政机关或上级行政机关有权撤销。', '', '', null, '', '0', '0');
