INSERT INTO `lp_question` VALUES ('120', '120', '00010001', '0', '判断题', '公民、法人或者其他组织的合法权益受到行政机关的具体行政行为侵犯，造成损害的，有权请求赔偿。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('121', '121', '00010001', '0', '判断题', '公民、法人或者其他组织对某一具体行政行为在法定期限内，不提起行政诉讼又不履行的，行政机关可以申请法院强制执行。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('122', '122', '00010001', '0', '判断题', '行政机关工作人员作出的具体行政行为侵犯公民的合法权益造成损害的，由该工作人员所在的行政机关负责赔偿。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('123', '123', '00010001', '0', '判断题', '两个以上行政机关作出同一具体行政行为的，共同作出具体行政行为的行政机关是共同被告。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('124', '124', '00010001', '0', '判断题', '人民法院审理行政案件，对滥用职权作出的具体行政行为，可以判决被告重新作出具体行政行为。', '', '', null, '', '0', '0');
