INSERT INTO `lp_question` VALUES ('126', '126', '00010001', '0', '判断题', '人民法院判决被告重新作出具体行政行为的，被告可以以同一事实和理由作出与原具体行政行为基本相同的具体行政行为。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('127', '127', '00010001', '0', '判断题', '《行政诉讼法》规定，被告无正当理由拒不到庭，或者未经法庭许可中途退庭的，可以缺席判决。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('128', '128', '00010001', '0', '判断题', '行政案件由最初作出具体行政行为的行政机关所在地的人民法院管辖。经复议的案件，复议机关改变原具体行政行为的，应当由复议机关所在地法院管辖。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('129', '129', '00010001', '0', '判断题', '行政诉讼中，对作出的具体行政行为负有举证责任的是人民法院。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('130', '130', '00010001', '0', '判断题', '根据《行政诉讼法》的规定，公民对于行政机关的行政不作为，有权提起行政诉讼。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('131', '131', '00010001', '0', '判断题', '根据《行政诉讼法》的规定，公民、法人或者其他组织对于外交行为、国防行为提起的诉讼，人民法院不予受理。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('132', '132', '00010001', '0', '判断题', '复议机关在法定期间内不作行政复议决定的，当事人对复议机关的不作为不服提起诉讼的，应以复议机关为被告。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('133', '133', '00010001', '0', '判断题', '在行政诉讼中，被告不得自行向原告和证人收集证据。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('134', '134', '00010001', '0', '判断题', '《行政诉讼法》规定，当事人对已经发生法律效力的判决或者裁定提出申诉后，原判决或者裁定应当暂停执行。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('135', '135', '00010001', '0', '判断题', '经行政复议的案件，复议机关改变原具体行政行为的，也可以由由复议机关所在地人民法院管辖。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('136', '136', '00010001', '0', '判断题', '在行政诉讼中，当事人不服人民法院第一审判决的，有权在判决书送达之日起15日内向上一级人民法院提出上诉。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('137', '137', '00010001', '0', '判断题', '行政许可直接涉及申请人与他人之间重大利益关系的，行政机关在作出行政许可决定前，应当告知申请人、利害关系人享有要求听证的权利；申请人、利害关系人在被告知听证权利之日起5日内提出听证申请的，行政机关应当在20日内组织听证。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('138', '138', '00010001', '0', '判断题', '《行政许可法》适用于有关行政机关对其他行政机关或者对其直接管理的事业单位的人事、财务、外事等事项的审批。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('139', '139', '00010001', '0', '判断题', '行政机关实施行政许可，依照法律、行政法规收取费用的，应当按照公布的法定项目和标准收费；所收取的费用必须全部上缴国库，任何机关或者个人不得以任何形式截留、挪用、私分或者变相私分。财政部门不得以任何形式向行政机关返还或者变相返还实施行政许可所收取的费用。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('140', '140', '00010001', '0', '判断题', '被许可人以欺骗、贿赂等不正当手段取得行政许可的，可以予以撤销。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('141', '141', '00010001', '0', '判断题', '《政府采购法》规定，在政府采购活动中，采购人员及相关人员与供应商有利害关系的，应当回避。供应商认为采购人员及相关人员与其他供应商有利害关系的，可以申请其回避。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('142', '142', '00010001', '0', '判断题', '《行政诉讼法》规定，因不动产提起的行政诉讼，由原告或被告住所地人民法院管辖。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('143', '143', '00010001', '0', '判断题', '《行政诉讼法》规定，行政诉讼中两个以上人民法院都有管辖权的案件，原告可以选择其中一个人民法院提起诉讼。原告向两个以上有管辖权的人民法院提起诉讼的，由最先立案的人民法管辖。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('144', '144', '00010001', '0', '判断题', '《行政诉讼法》规定，因不动产提起诉讼的案件从行政行为作出之日起超过二十年，其他案件从行政行为作出之日起超过五年提起诉讼的，人民法院不予受理。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('145', '145', '00010001', '0', '判断题', '有权申请行政复议的公民死亡的，其他人无权代为申请行政复议。有权申请行政复议的公民为无民事行为能力人或者限制民事行为能力人的，其法定代理人可以代为申请行政复议。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('146', '146', '00010001', '0', '判断题', '对地方各级人民政府的具体行政行为不服的，可以向上一级地方人民政府申请行政复议。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('147', '147', '00010001', '0', '判断题', '在行政复议过程中，被申请人可以自行向申请人和其他有关组织或者个人收集证据。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('148', '148', '00010001', '0', '判断题', '行政复议申请人应当以书面形式申请行政复议，不能以口头形式申请。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('149', '149', '00010001', '0', '判断题', '情况复杂，不能在规定期限内作出行政复议决定的，经行政复议机关负责人批准，可以适当延长行政复议期限，但延长期限最多不超过30日。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('150', '150', '00010001', '0', '判断题', '公民认为行政机关不依法履行法定职责的，不能申请行政复议。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('151', '151', '00010001', '0', '判断题', '公务员不服人事处理决定的，不能申请行政复议。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('152', '152', '00010001', '0', '判断题', '公民、法人或者其他组织向人民法院提起行政诉讼，人民法院已经依法受理的，不得申请行政复议。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('153', '153', '00010001', '0', '判断题', '行政复议机关应当审查申请行政复议的具体行政行为的合法性与适当性。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('154', '154', '00010001', '0', '判断题', '行政强制措施由法律、法规规定的行政机关在法定职权范围内实施。行政强制措施权不得委托。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('155', '155', '00010001', '0', '判断题', '被申请人不提交当初作出具体行政行为的证件，依据和其他有关材料的，行政复议机关可以撤销原具体行政行为。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('156', '156', '00010001', '0', '判断题', '不服行政机关对民事纠纷作出的调解的，可以申请行政复议。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('157', '157', '00010001', '0', '判断题', '发展社会主义民主政治，最根本的是坚持党的领导、人民当家作主和依法治国的有机统一。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('158', '158', '00010001', '0', '判断题', '行政机关作出强制执行决定前，应当事先催告当事人履行义务。催告应当以书面形式作出。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('159', '159', '00010001', '0', '判断题', '《行政强制法》规定，划拨存款、汇款应当由法律规定的行政机关决定，并书面通知金融机构。金融机构接到行政机关依法作出划拨存款、汇款的决定后，应当立即划拨。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('160', '160', '00010001', '0', '判断题', '在行政诉讼中，人民法院如果发现行政法规和行政规章与宪法、法律相抵触，可以撤销。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('161', '161', '00010001', '0', '判断题', '行政机关应当于举行听证的七日前将举行听证的时间、地点通知申请人、利害关系人，必要时予以公告。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('162', '162', '00010001', '0', '判断题', '依法拍卖财物，由行政机关委托拍卖机构依照《中华人民共和国拍卖法》的规定办理。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('163', '163', '00010001', '0', '判断题', '具体行政行为认定事实不清的，证据不足的，行政复议机关可以调查补正后作出维持决定。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('164', '164', '00010001', '0', '判断题', '行政机关工作人员办理行政许可、实施监督检查，索取或者收受他人财物或者谋取其他利益，构成犯罪的，依法追究刑事责任。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('165', '165', '00010001', '0', '判断题', '行政机关依法作出不予行政许可的书面决定的，可以不说明理由，但要告知申请人享有依法申请行政复议或者提起行政诉讼的权利。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('166', '166', '00010001', '0', '判断题', '行政机关在调查或者进行检查时，执法人员不得少于两人，并应当向当事人或者有关人员出示证件。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('167', '167', '00010001', '0', '判断题', '行政复议机关受理行政复议申请，可以向申请人收取一定数额的行政复议费用。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('168', '168', '00010001', '0', '判断题', '在行政诉讼中，作为被告的行政机关法定代表人可以出庭参加诉讼，也可以委托代理人参加诉讼。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('169', '169', '00010001', '0', '判断题', '行政诉讼中的第三人只能通过申请参加到行政诉讼中来。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('170', '170', '00010001', '0', '判断题', '作出罚款决定的行政机关应当与收缴罚款的机构分离。作出行政处罚决定的行政机关及其执法人员在任何情况下均不得自行收缴罚款。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('171', '171', '00010001', '0', '判断题', '限制人身自由的行政处罚权即行政拘留的决定权只能由公安机关行使。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('172', '172', '00010001', '0', '判断题', '国家为了公共利益的需要，可以依照法律规定对公民的私有财产实行征收或者征用并给予补偿。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('173', '173', '00010001', '0', '判断题', '行政机关申请人民法院强制执行，不缴纳申请费。强制执行的费用由被执行人承担。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('174', '174', '00010001', '0', '判断题', '行政机关依申请提供政府信息，除可以收取检索、复制、邮寄等成本费用外，不得收取其他费用。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('175', '175', '00010001', '0', '判断题', '行政诉讼法规定，当事人对已发生法律效力的判决、裁定，认为确有错误的，可以向原审人民法院或者上一级人民法院提出申诉，判决、裁定停止执行。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('176', '176', '00010001', '0', '判断题', '对县级以上地方各级人民政府工作部门的具体行政行为不服的，由申请人选择，可以向该部门的本级人民政府申请行政复议，也可以向上一级主管部门申请行政复议。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('177', '177', '00010001', '0', '判断题', '被告行政机关委托的律师可以向原告和证人收集证据。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('178', '178', '00010001', '0', '判断题', '公民、法人或者其他组织认为行政机关和行政机关工作人员的具体行政行为侵犯其合法权益，有权依照《行政诉讼法》向人民法院提起诉讼。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('179', '179', '00010001', '0', '判断题', '行政复议既审查行政行为的合法性，又审查行政行为的合理性。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('180', '180', '00010001', '0', '判断题', '法律、行政法规设定的行政许可，其适用范围没有地域限制的，申请人取得的行政许可在全国范围内有效。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('181', '181', '00010001', '0', '判断题', '行政诉讼被告负举证责任，但不排除对某些事项应当由原告提供证据。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('182', '182', '00010001', '0', '判断题', '行政处罚由被处罚人所在地的县级以上地方人民政府具有行政处罚权的行政机关管辖。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('183', '183', '00010001', '0', '判断题', '凡是侵犯公民、法人和其他组织合法权益的具体行政行为，均属于行政复议的受理范围。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('184', '184', '00010001', '0', '判断题', '行政相对人对规章以下的规定不服，可就该规定单独申请行政复议。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('185', '185', '00010001', '0', '判断题', '行政机关实施行政许可，不得收取任何费用，但行政机关提供行政许可申请书格式文本的除外。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('186', '186', '00010001', '0', '判断题', '行政行为的撤销是因行政机关的过错引起，并给行政相对人造成损害的，则行政机关应负赔偿责任。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('187', '187', '00010001', '0', '判断题', '行政诉讼不适用调解是指对诉讼中任何问题均不得进行调解。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('188', '188', '00010001', '0', '判断题', '谁主张、谁举证是举证责任分配的一般原则，行政诉讼也不例外。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('189', '189', '00010001', '0', '判断题', '行政机关发现自己作出的行政行为有错可以依法自行撤销或改变行政行为，但有些具体行政行为的撤销或变更给相对人造成损失的，应当依法赔偿。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('190', '190', '00010001', '0', '判断题', '罚款主要适用于以牟取非法经济利益为目的的行政违法行为。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('191', '191', '00010001', '0', '判断题', '在行政复议期间，申请人认为需要停止执行的具体行政行为可以停止执行。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('192', '192', '00010001', '0', '判断题', '公安干警在下班期间制止犯罪的行为是公务行为。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('193', '193', '00010001', '0', '判断题', '内部行政行为可以适用行政申诉，但不得提起行政诉讼。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('194', '194', '00010001', '0', '判断题', '法律没有规定行政机关强制执行的，作出行政决定的行政机关应当申请人民法院强制执行。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('195', '195', '00010001', '0', '判断题', '并非所有的行政行为都有强制执行的效力。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('196', '196', '00010001', '0', '判断题', '设定和实施行政处罚必须以事实为依据，与违法行为的事实、性质、情节以及社会危害程度相当。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('197', '197', '00010001', '0', '判断题', '对违法行为给予行政处罚的规定必须公布；未经公布的，不得作为行政处罚的依据。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('198', '198', '00010001', '0', '判断题', '行政机关无权撤销自己作出的行政行为。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('199', '199', '00010001', '0', '判断题', '违法行为在两年内未被发现的，不再给予行政处罚。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('200', '200', '00010001', '0', '判断题', '如当事人有申辩行为，行政机关可因其认错态度而加重处罚。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('201', '201', '00010001', '0', '判断题', '行政处罚适用简易程序时可对违法行为当场进行处罚。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('202', '202', '00010001', '0', '判断题', '行政机关采取行政强制措施，必须有法律、法规的明确授权，并严格依照法律规定实施。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('203', '203', '00010001', '0', '判断题', '未依法说明不受理行政许可申请或者不予行政许可的理由的，由其上级行政机关或者监察机关责令改正；情节严重的，对直接负责的主管人员和其他直接责任人员依法给予行政处分。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('204', '204', '00010001', '0', '判断题', '行政机关及其执法人员当场收缴罚款的，必须向当事人出具省、自治区、直辖市财政部门统一制发的罚款收据；不出具财政部门统一制发的罚款收据的，当事人有权拒绝缴纳罚款。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('205', '205', '00010001', '0', '判断题', '行政机关为牟取本单位私利，对应当依法移交司法机关追究刑事责任的不移交，以行政处罚代替刑罚，由上级行政机关或者有关部门责令纠正；拒不纠正的，对直接负责的主管人员给予行政处分；徇私舞弊、包庇纵容违法行为的，按照刑法相关规定处理。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('206', '206', '00010001', '0', '判断题', '当事人确有经济困难，需要延期或者分期缴纳罚款的，经当事人申请和行政机关批准，应当暂缓或者分期缴纳。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('207', '207', '00010001', '0', '判断题', '只要当事人对行政处罚决定不服申请行政复议或者提起行政诉讼的，行政处罚一律停止执行。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('208', '208', '00010001', '0', '判断题', '被申请人不履行或者无正当理由拖延履行行政复议决定的，对直接负责的主管人员和其他直接责任人员依法给予警告、记过、记大过的行政处分；经责令履行仍拒不履行的，依法给予降级、撤职、开除的行政处分。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('209', '209', '00010001', '0', '判断题', '行政复议机构中止、恢复行政复议案件的审理，可以告知有关当事人。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('210', '210', '00010001', '0', '判断题', '公民、法人或者其他组织对行政机关行使法律、法规规定的自由裁量权作出的具体行政行为不服申请行政复议的，行政复议机关可以按照自愿、合法的原则进行调解。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('211', '211', '00010001', '0', '判断题', '赔偿义务机关赔偿损失后，应当责令有过失的工作人员或者受委托的组织或者个人承担部分或者全部赔偿费用。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('212', '212', '00010001', '0', '判断题', '赔偿请求人可以向共同赔偿义务机关中的任何一个赔偿义务机关要求赔偿，该赔偿义务机关应当先予赔偿。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('213', '213', '00010001', '0', '判断题', '赔偿请求人根据受到的不同损害，不可以同时提出数项赔偿要求。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('214', '214', '00010001', '0', '判断题', '经复议机关复议的，最初造成侵权行为的行政机关为赔偿义务机关，但复议机关的复议决定加重损害的，复议机关对加重的部分履行赔偿义务。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('215', '215', '00010001', '0', '判断题', '赔偿请求人请求国家赔偿的时效为3年，自国家机关及其工作人员行使职权时的行为被依法确认为违法之日起计算，但被羁押期间不计算在内。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('216', '216', '00010001', '0', '判断题', '经上级行政机关复议的行政案件，复议机关改变原具体的行政行为的，也可由复议机关所在地的人民法院管辖。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('217', '217', '00010001', '0', '判断题', '《行政诉讼法》规定，当事人一方或者双方为二人以上，因同一行政行为发生的行政案件，或者因同类行政行为发生的行政案件,人民法院认为可以合并审理的，为共同诉讼。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('218', '218', '00010001', '0', '判断题', '行政诉讼中，没有诉讼行为能力的公民，由其法定代理人代为诉讼。法定代理人互相推诿代理责任的，由人民法院指定其中一人代为诉讼。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('219', '219', '00010001', '0', '判断题', '在行政诉讼中，原告对作出的具体行政行为负有举证责任，被告应当提供作出该具体行政行为的证据和所依据的规范性文件。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('220', '220', '00010001', '0', '判断题', '原告可以提供证明具体行政行为违法的证据。原告提供的证据不成立的，可以免除被告的举证责任。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('221', '221', '00010001', '0', '判断题', '人民法院审理行政案件，参照规章；认为规章之间不一致的，由最高人民法院送请国务院作出判决。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('222', '222', '00010001', '0', '判断题', '适用简易程序审理的行政案件，由审判员一人独任审理。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('223', '223', '00010001', '0', '判断题', '违反法定程序不是人民法院撤销具体行政行为的标准。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('224', '224', '00010001', '0', '判断题', '同级人大及其常委会无权撤销同级政府的抽象行政行为。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('225', '225', '00010001', '0', '判断题', '地方性法规可以设定除限制人身自由以外的处罚。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('226', '226', '00010001', '0', '判断题', '地方性法规适用于本行政区域内发生的行政案件。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('227', '227', '00010001', '0', '判断题', '发展循环经济应当在技术可行、经济合理和有利于节约资源、保护环境的前提下，按照资源化优先的原则实施。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('228', '228', '00010001', '0', '判断题', '生产列入强制回收名录的产品或者包装物的企业，必须对废弃的产品或者包装物负责回收。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('229', '229', '00010001', '0', '判断题', '回收的电器电子产品，需要拆解和再生利用的，应当交售给生产企业。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('230', '230', '00010001', '0', '判断题', '从事工艺、设备、产品及包装物设计，应当按照减少资源消耗和废物产生的要求，优先选择采用易回收、易拆解、易降解、无毒无害或者低毒低害的材料和设计方案，并应当符合有关国家标准的强制性要求。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('231', '231', '00010001', '0', '判断题', '任何单位和个人不得将产生严重污染的生产设备转移给没有污染防治能力的单位使用。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('232', '232', '00010001', '0', '判断题', '《环境保护法》规定，从严治理是环境保护坚持的原则。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('233', '233', '00010001', '0', '判断题', '跨行政区的环境污染和环境破坏的防治工作，由有关地方人民政府协商解决，或者由上级人民政府协调解决，作出决定。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('234', '234', '00010001', '0', '判断题', '建设项目中防治污染的设施，必须与主体工程同时勘察、同时设计、同时施工。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('235', '235', '00010001', '0', '判断题', '《环境保护法》规定，6月5日是中华人民共和国环境日。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('236', '236', '00010001', '0', '判断题', '对于涉及环境犯罪的案件，可以行政处罚取代刑事处罚。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('237', '237', '00010001', '0', '判断题', '认真落实环境保护法律法规，严厉查处破坏生态、污染环境的案件，强化经济和刑事责任追究。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('238', '238', '00010001', '0', '判断题', '社区矫正人员由公安局和派出所负责监管。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('239', '239', '00010001', '0', '判断题', '管制是一种剥夺犯罪分子人身自由的刑罚。判处管制的犯罪分子，依法实行社区矫正。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('240', '240', '00010001', '0', '判断题', '《不动产登记暂行条例》规定，国家实行不动产统一登记制度。不动产登记遵循严格管理、稳定连续、方便群众的原则。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('241', '241', '00010001', '0', '判断题', '《不动产登记暂行条例》规定，查询不动产登记资料的单位、个人应当向不动产登记机构说明查询目的，不得将查询获得的不动产登记资料用于其他目的；未经权利人同意，不得泄露查询获得的不动产登记资料。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('242', '242', '00010001', '0', '判断题', '《不动产登记暂行条例》规定，伪造、变造不动产权属证书、不动产登记证明，或者买卖、使用伪造、变造的不动产权属证书、不动产登记证明的，由不动产登记机构或者公安机关依法予以收缴。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('243', '243', '00010001', '0', '判断题', '人民警察在执行上级的决定和命令过程中，认为决定和命令有错误的，可以按照规定提出意见，并可以中止或者改变决定和命令的执行。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('244', '244', '00010001', '0', '判断题', '人民警察对超越法律、法规规定的人民警察职责范围的指令，无权拒绝执行。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('245', '245', '00010001', '0', '判断题', '遇有拒捕、暴乱、越狱、抢夺枪支或者其他暴力行为的紧急情况，人民警察依照国家有关规定可以使用武器。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('246', '246', '00010001', '0', '判断题', '人民警察在非工作时间，遇有其职责范围内的紧急情况，应当履行职责。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('247', '247', '00010001', '0', '判断题', '公民和组织因协助人民警察执行职务，造成人身伤亡或者财产损失的，应当按照国家有关规定给予抚恤或者补偿。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('248', '248', '00010001', '0', '判断题', '人民警察遇到公民人身、财产安全受到侵犯或者处于其他危难情形，应当立即救助；对公民提出解决纠纷的要求，应当给予帮助；对公民的报警案件，应当及时查处。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('249', '249', '00010001', '0', '判断题', '劳动者是具有劳动能力，以从事劳动获取合法劳动报酬的自然人和法人。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('250', '250', '00010001', '0', '判断题', '根据《劳动合同法》规定，劳动关系自用人单位与劳动者签订劳动合同开始确立。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('251', '251', '00010001', '0', '判断题', '根据《劳动法》规定，劳动合同中的劳动条件和劳动报酬标准，应该相当于集体合同中的标准。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('252', '252', '00010001', '0', '判断题', '童工是指被用人单位招用的未满14周岁的未成年人。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('253', '253', '00010001', '0', '判断题', '从事特种作业的劳动者必须经过专门培训并取得特种作业资格。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('254', '254', '00010001', '0', '判断题', '用人单位未依法为劳动者缴纳社会保险费的，劳动者可以解除劳动合同。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('255', '255', '00010001', '0', '判断题', '用人单位应当按照劳动合同约定和国家规定，向劳动者及时支付劳动报酬。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('256', '256', '00010001', '0', '判断题', '扣押劳动者证件的，由法院责令限期退还劳动者本人，并依照有关法律规定给予处罚。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('257', '257', '00010001', '0', '判断题', '劳务派遣单位应当与被派遣劳动者订立1年以上的固定期限劳动合同，按月支付劳动报酬；被派遣劳动者在无工作期间，劳务派遣单位应当按照所在地人民政府规定的最低工资标准，向其按月支付报酬。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('258', '258', '00010001', '0', '判断题', '同一用人单位与同一劳动者可以约定两次试用期。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('259', '259', '00010001', '0', '判断题', '订立和变更劳动合同，应当遵循平等自愿、协商一致的原则，不得违反法律、行政法规的规定。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('260', '260', '00010001', '0', '判断题', '用人单位发生合并或者分立等情况，原劳动合同继续有效。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('261', '261', '00010001', '0', '判断题', '经济补偿是指解除劳动合同后，用人单位给劳动者以经济上的补助。一般不包括生活补助费和医疗补助费。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('262', '262', '00010001', '0', '判断题', '我国《劳动合同法》规定，试用期包含在劳动合同期限内，并根据劳动合同期限的长短确定试用期的长短。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('263', '263', '00010001', '0', '判断题', '根据《就业促进法》规定，职业中介机构根据需要可以扣押劳动者的居民身份证和其他证件，或者向劳动者收取押金。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('264', '264', '00010001', '0', '判断题', '我国规定领取失业保险金的期限最多不超过12个月。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('265', '265', '00010001', '0', '判断题', '公共就业服务机构应当不断提高服务的质量和效率，不得从事经营性活动。公共就业服务经费纳入同级财政预算。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('266', '266', '00010001', '0', '判断题', '当事人申请劳动争议调解应当书面申请，不得口头申请。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('267', '267', '00010001', '0', '判断题', '职工在上下班途中，受到意外伤害的应当认定为工伤。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('268', '268', '00010001', '0', '判断题', '劳动者提前30日以书面形式通知用人单位的，可以解除劳动合同。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('269', '269', '00010001', '0', '判断题', '劳动者可拒绝违章指挥和强令进行没有职业病防护措施的作业。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('270', '270', '00010001', '0', '判断题', '中华人民共和国民法调整公民之间、法人之间、公民与法人之间的财产关系和人身关系。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('271', '271', '00010001', '0', '判断题', '民事法律关系的主体是指参加民事法律关系，享有民事权利，承担民事义务的特定的当事人。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('272', '272', '00010001', '0', '判断题', '监护人不履行监护职责或者侵害被监护人的合法利益的，应当承担责任；给被监护人造成财产损失的，应当赔偿损失。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('273', '273', '00010001', '0', '判断题', '法人或者其他组织的法定代表人、负责人超越权限的行为，除相对人知道或者应当知道其超越权限的以外，该代表行为有效。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('274', '274', '00010001', '0', '判断题', '李某被宣告死亡后又重新出现，经申请法院撤销了对他的死亡宣告，其配偶再婚后配偶又死亡的，李某与原配偶之间的夫妻关系自行恢复。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('275', '275', '00010001', '0', '判断题', '诉讼时效期间届满，权利人仍不行使其民事权利的，其民事权利即归于消灭。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('276', '276', '00010001', '0', '判断题', '共有人对共有的不动产或者动产，没有约定为按份共有或者共同共有，或者约定不明确的，除共有人具有家庭关系等外，视为按份共有。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('277', '277', '00010001', '0', '判断题', '抵押权人和抵押人在订立抵押合同时，可以约定在债务履行期届满抵押权人未受清偿时，抵押物的所有权转移为债权人所有。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('278', '278', '00010001', '0', '判断题', '人身权是和生命联系在一起的，法人是组织体，谈不上什么人身。因此，没有人身权。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('279', '279', '00010001', '0', '判断题', '当事人订立合同，应当具有相应的民事权利能力和民事行为能力。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('280', '280', '00010001', '0', '判断题', '无处分权的人处分他人财产，经权利人追认或者无处分权的人订立合同后取得处分权的，该合同有效。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('281', '281', '00010001', '0', '判断题', '当事人约定由债务人向第三人履行债务的，债务人未向第三人履行债务或者履行债务不符合约定的，应当向第三人承担违约责任。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('282', '282', '00010001', '0', '判断题', '公民的合法财产受法律保护，禁止任何组织或者个人侵占、哄抢、破坏或者非法查封、扣押、冻结、没收。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('283', '283', '00010001', '0', '判断题', '债权人转让债权应当经债务人同意，未经债务人同意，该转让对债务人不发生效力。', '', '', null, '', '0', '0');
