INSERT INTO `lp_question` VALUES ('362', '362', '00010001', '0', '判断题', '青岛市李沧区人民代表大会代表是由选民间接选举产生的。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('363', '363', '00010001', '0', '判断题', '妇女享有与男子平等的选举权和被选举权。居民委员会、村民委员会成员中，妇女应当有适当的名额。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('364', '364', '00010001', '0', '判断题', '《立法法》规定，授权国务院可以根据实际需要，对其中的部分事项先制定行政法规，授权决定应当明确授权的目的、范围。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('365', '365', '00010001', '0', '判断题', '《立法法》规定，授权国务院制定行政法规的，授权的期限不得超过五年，但是授权决定另有规定的除外。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('366', '366', '00010001', '0', '判断题', '立法应当依照法定的权限和程序，从国家整体利益出发，维护社会主义法制的统一和尊严。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('367', '367', '00010001', '0', '判断题', '全国人民代表大会及其常务委员会可以根据改革发展的需要作出决定，就特定事项在部分地方暂停适用法律的部分规定。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('368', '368', '00010001', '0', '判断题', '立法应当从实际出发，适应经济社会发展要求，科学合理地规定公民、法人和其他组织的权利与义务、国家机关的权力与责任，做到法律规范明确、具体，具有可执行性和可操作性。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('369', '369', '00010001', '0', '判断题', '《立法法》规定，列入常务委员会会议议程的法律案，法律委员会、有关的专门委员会和常务委员会工作机构应当听取各方面的意见。听取意见可以采取座谈会、论证会、听证会等多种形式。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('370', '370', '00010001', '0', '判断题', '法律案有关问题具有较强的专业性或者需要进行可行性评价的，可以召开论证会，听取专家、有关部门等方面的意见。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('371', '371', '00010001', '0', '判断题', '法律案有关问题存在重大意见分歧或者涉及利益关系重大调整的，可以依申请召开听证会，听取利害关系人、有关部门、专家等方面的意见。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('372', '372', '00010001', '0', '判断题', '列入常务委员会会议议程的法律案，应当将法律草案文本及其说明等通过网络等媒介向社会公布，征求意见，但是经委员长会议决定不予公布的除外。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('373', '373', '00010001', '0', '判断题', '法律草案修改稿经常务委员会会议审议，经委员长会议决定，常务委员会会议可以对个别有重大意见分歧的条文先予表决。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('374', '374', '00010001', '0', '判断题', '全国人民代表大会常务委员会应当依照宪法和法律的规定，加强对立法工作的组织协调，充分发挥在立法中的主导作用。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('375', '375', '00010001', '0', '判断题', '全国人民代表大会常务委员会通过每届任期的立法规划、年度立法计划等形式，加强对立法工作的统筹安排。立法规划和年度立法计划由委员长会议通过并向社会公布。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('376', '376', '00010001', '0', '判断题', '全国人民代表大会负责编制立法规划和拟订年度立法计划，并协助全国人民代表大会常务委员会督促立法规划和年度立法计划的落实。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('377', '377', '00010001', '0', '判断题', '法律被废止的，除由其他法律规定废止该法律的以外，由国家主席签署主席令予以公告。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('378', '378', '00010001', '0', '判断题', '全国人民代表大会有关的专门委员会、常务委员会工作机构根据法律实施的情况，可以组织对有关法律开展立法后评估，并向常务委员会提交报告。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('379', '379', '00010001', '0', '判断题', '青岛市制定地方性法规限于城市建设、市容卫生、环境保护等城市管理方面的事项。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('380', '380', '00010001', '0', '判断题', '规定本行政区域特别重大或者社会普遍关注事项的地方性法规，应当由人民代表大会通过。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('381', '381', '00010001', '0', '判断题', '全国人大通过法律案，应由全体代表的三分之二赞同表决才能通过。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('382', '382', '00010001', '0', '判断题', '全国人民代表大会和全国人民代表大会常务委员会行使国家立法权。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('383', '383', '00010001', '0', '判断题', '民族区域自治地方的人大常委会可以制定自治条例和单行条例。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('384', '384', '00010001', '0', '判断题', '在国家尚未制定法律、行政法规的领域，地方性法规可以在法定权限内进行先行性立法。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('385', '385', '00010001', '0', '判断题', '罪刑法定原则是我国刑法的基本原则之一，其基本含义是：法律明文规定为犯罪行为的，依照法律定罪处罚，法律没有明文规定为犯罪行为的，不得定罪处罚。其著名的格言是“法无明文规定不为罪，法无明文规定不处罚”。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('386', '386', '00010001', '0', '判断题', '根据我国《刑法》的规定，任何犯罪的成立都必须具备以下四个方面的要件：犯罪客体、犯罪客观方面、犯罪主体、犯罪主观方面。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('387', '387', '00010001', '0', '判断题', '国家机关实施危害社会的行为，例如私分国有资产、私分罚没财物、受贿、向其他单位行贿等，即使具有严重的社会危害性，也不构成犯罪。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('388', '388', '00010001', '0', '判断题', '对任何人犯罪，在适用法律上人人平等。不允许任何人有超越法律的特权。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('389', '389', '00010001', '0', '判断题', '年满30岁的精神病人张某，先后采用暴力手段抢劫他人的手机三部，对张某应当以抢劫罪追究刑事责任。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('390', '390', '00010001', '0', '判断题', '我国《刑法》中的正当防卫，既是法律赋予公民的一项权利，也是公民应尽的法律义务。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('391', '391', '00010001', '0', '判断题', '所谓国家安全，即我国人民民主专政政权和社会主义制度不受侵犯，我国的国家主权和领土完整不受侵犯。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('392', '392', '00010001', '0', '判断题', '我国《刑法》的紧急避险中关于避免本人危险的规定，可以适用于职业上、业务上负有特定责任的人。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('393', '393', '00010001', '0', '判断题', '李某将孙某推入水中，意图将其淹死，但听到孙某可怜的呼喊声，又将孙某救起并脱下自己的毛衣毛裤给孙某穿。李某的行为属于犯罪中止。对李某应当免除刑罚处罚。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('394', '394', '00010001', '0', '判断题', '我国《刑法》中的共同犯罪，是指二人以上同时实施犯罪。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('395', '395', '00010001', '0', '判断题', '刑法第规定教唆不满十八周岁的人犯罪的,应当从重处罚。如果被教唆的人没有犯被教唆的罪,对于教唆犯,可以从轻或者减轻处罚。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('396', '396', '00010001', '0', '判断题', '无期徒刑虽然没有刑期限制，但在具体执行中，并不一定将犯罪分子关押到死，而是充分给其悔过自新、重新回归社会的机会。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('397', '397', '00010001', '0', '判断题', '根据我国《刑法》规定，被判处管制刑以及被判处拘役、有期徒刑而宣告缓刑的犯罪分子，由司法行政机关负责执行或者进行考察。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('398', '398', '00010001', '0', '判断题', '我国《刑法》中的免除处罚，就是对行为人只定罪而不判刑。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('399', '399', '00010001', '0', '判断题', '某局机关会计冯某因贪污公款被立案侦查，其间，冯某检举分管副局长受贿30万元的犯罪行为，并经查证属实。冯某的行为构成重大立功，可以减轻或者免除处罚。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('400', '400', '00010001', '0', '判断题', '被宣告缓刑的犯罪分子，在缓刑考验期限内，如果出现法律规定应当撤销缓刑的事由，就应当收监执行原判刑罚或者数罪并罚。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('401', '401', '00010001', '0', '判断题', '魏某财迷心窍，以扬言披露姜某隐私的要挟方法勒索了5000元钱。事后姜某告发了魏某，公安机关以涉嫌敲诈勒索罪对他立案侦查。正当魏某不知如何是好时，他的一位朋友说可以到外面避避风头，过几年就没事了。该朋友的说法是对的，因为我国《刑法》中有追诉时效（即追究犯罪分子刑事责任的有效期限）的规定。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('402', '402', '00010001', '0', '判断题', '放火烧毁自己或家庭所有的房屋以及财物的，不可能构成放火罪。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('403', '403', '00010001', '0', '判断题', '无论是从事交通运输的人员，还是非交通运输人员，都可以构成交通肇事罪。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('404', '404', '00010001', '0', '判断题', '国家机关工作人员郑某于2000年与女青年戴某登记结婚。婚后，由于戴某生的是女儿，郑某开始嫌弃她们母女，在外面另寻新欢。2004年年初，郑某在宴会上结识女青年刘某，不久就同居生活，时间长达5年之久，其间，刘某为郑某生了个儿子。由于郑某与刘某并未登记结婚，只是形成了事实婚姻，而我国法律是不承认事实婚姻的，因此，郑某的行为不构成重婚罪。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('405', '405', '00010001', '0', '判断题', '国家机关或者金融、电信、交通、教育、医疗等单位的工作人员，违反国家规定，将本单位在履行职责或者提供服务过程中获得的公民个人信息，出售或者非法提供给他人，即使情节严重的，也不构成犯罪。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('406', '406', '00010001', '0', '判断题', '以暴力、威胁方法阻碍全国人民代表大会和地方各级人民代表大会代表依法执行代表职务的，处三年以下有期徒刑、拘役、管制或者罚金。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('407', '407', '00010001', '0', '判断题', '人民检察院对侦查终结的案件进行审查后，应当根据案件情况，分别作出起诉、免于起诉或者不起诉的决定。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('408', '408', '00010001', '0', '判断题', '公安机关、人民检察院、人民法院认为犯罪嫌疑人、被告人有犯罪事实并且需要追究刑事责任的，应当立案。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('409', '409', '00010001', '0', '判断题', '第一审法院判决被告人无罪的，如果被告人在押，在判决生效后，应当立即释放。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('410', '410', '00010001', '0', '判断题', '犯罪嫌疑人在被侦查机关第一次讯问后，有权委托辩护人。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('411', '411', '00010001', '0', '判断题', '对于正在实行犯罪或者犯罪后即时被发觉的人，任何公民都可以立即将其扭送至公检法机关处理。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('412', '412', '00010001', '0', '判断题', '侦查机关根据侦查工作的需要设立的鉴定机构，必要时可以面向社会接受委托从事司法鉴定业务。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('413', '413', '00010001', '0', '判断题', '被害人由于被告人的犯罪行为遭受物质损失的，在刑事诉讼过程中，有权提起附带民事诉讼。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('414', '414', '00010001', '0', '判断题', '受国家机关、国有公司、企业、事业单位、人民团体委托管理、经营国有财产的人员，利用职务上的便利，侵吞、窃取、骗取或者以其他手段非法占有国有财务的，以贪污论。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('415', '415', '00010001', '0', '判断题', '证人、鉴定人、被害人认为因在诉讼中作证，本人或者其近亲属的人身安全面临危险的，可以向人民法院、人民检察院、公安机关请求予以保护。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('416', '416', '00010001', '0', '判断题', '我国专门的法律监督机关是人民检察院。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('417', '417', '00010001', '0', '判断题', '犯罪是指人的一种危害社会的、触犯刑律的、应受刑罚处罚的行为。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('418', '418', '00010001', '0', '判断题', '公安机关拘留人的时候，必须出示拘留证。拘留后，应当立即将被拘留人送看守所羁押，至迟不得超过二十四小时。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('419', '419', '00010001', '0', '判断题', '贪污贿赂犯罪，国家工作人员的渎职犯罪，由人民检察院立案侦查，国家机关工作人员利用职权实施的非法拘禁、刑讯逼供由公安机关立案侦查。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('420', '420', '00010001', '0', '判断题', '对由于故意犯罪曾受过一定的刑罚处罚的，在其刑罚执行完毕或被赦免以后，在法定期限（5年）内又故意犯一定之罪的罪犯是累犯，累犯不适用缓刑。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('421', '421', '00010001', '0', '判断题', '刑事拘留是公安机关限制违反治安管理秩序的行为人短期人身自由的一种处罚。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('422', '422', '00010001', '0', '判断题', '被告人上诉，人民检察院也提起抗诉的不受“上诉不加刑”原则的制约。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('423', '423', '00010001', '0', '判断题', '违法行为构成犯罪，应当依法追究刑事责任，不得以行政处罚代替刑事处罚。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('424', '424', '00010001', '0', '判断题', '羁押工作仅限于对被依法判处有期徒刑罪犯进行关押看守的工作。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('425', '425', '00010001', '0', '判断题', '人民检察院决定逮捕犯罪嫌疑人、被告人的，由人民检察院执行。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('426', '426', '00010001', '0', '判断题', '对于人民检察院批准逮捕的决定，公安机关应当执行，并且在适当时将执行情况通知人民检察院。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('427', '427', '00010001', '0', '判断题', '人民检察院应当自接到公安机关提请逮捕后的7日内作出决定。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('428', '428', '00010001', '0', '判断题', '犯罪嫌疑人在侦查阶段聘请律师只能为其提供法律咨询。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('429', '429', '00010001', '0', '判断题', '犯罪嫌疑人自被侦查机关第一次讯问或者采取强制措施之日起，有权委托辩护人；在侦查期间，只能委托律师作为辩护人。被告人有权随时委托辩护人。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('430', '430', '00010001', '0', '判断题', '讯问犯罪嫌疑人的侦查人员不得少于3人。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('431', '431', '00010001', '0', '判断题', '询问证人是侦查人员依照法定程序了解案件情况的诉讼活动。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('432', '432', '00010001', '0', '判断题', '对可能被判处无期徒刑、死刑的第一审刑事案件，受理的法院是中级人民法院。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('433', '433', '00010001', '0', '判断题', '询问不满18周岁的证人，应当通知其法定代理人到场。', '', '', null, '', '0', '0');
INSERT INTO `lp_question` VALUES ('434', '434', '00010001', '0', '判断题', '《预算法》规定，各级政府应当建立跨年度预算平衡机制。', '', '', null, '', '0', '0');
