INSERT INTO `pufa_import2` VALUES ('3', '3', '判断题', '在产品保修期内，经一次修理后仍不能正常使用的，经营者应当负责更换或退货。', '对', '错', '', '', '', 'B', '消费者权益保护法');
INSERT INTO `pufa_import2` VALUES ('4', '4', '判断题', '《消费者权益保护法》规定，消费者在购买、使用商品和接受服务时享有人身安全不受损害的权利，不包括财产不受损害的权利。', '对', '错', '', '', '', 'B', '消费者权益保护法');
INSERT INTO `pufa_import2` VALUES ('5', '5', '判断题', '《消费者权益保护法》规定，经营者提供的机动车、计算机、电视机、电冰箱、空调器、洗衣机等耐用商品或者装饰装修等服务，消费者自接受商品或者服务之日起六个月内发现瑕疵，发生争议的，由经营者承担有关瑕疵的举证责任。', '对', '错', '', '', '', 'A', '消费者权益保护法');
INSERT INTO `pufa_import2` VALUES ('6', '6', '判断题', '《消费者权益保护法》规定，经营者提供的商品或者服务不符合质量要求的，消费者可以依照国家规定、当事人约定退货，或者要求经营者履行更换、修理等义务。没有国家规定和当事人约定的，消费者可以自收到商品之日起七日内退货。', '对', '错', '', '', '', 'A', '消费者权益保护法');
INSERT INTO `pufa_import2` VALUES ('7', '7', '判断题', '《消费者权益保护法》规定，消费者向有关行政部门投诉的，该部门应当自收到投诉之日起七日内，予以处理并告知消费者。', '对', '错', '', '', '', 'B', '消费者权益保护法');
INSERT INTO `pufa_import2` VALUES ('8', '8', '判断题', '《消费者权益保护法》规定，经营者提供商品或者服务有欺诈行为的，应当按照消费者的要求增加赔偿其受到的损失，增加赔偿的金额为消费者购买商品的价款或者接受服务的费用的三倍；增加赔偿的金额不足五百，为五百。', '对', '错', '', '', '', 'A', '消费者权益保护法');
INSERT INTO `pufa_import2` VALUES ('9', '9', '判断题', '《消费者权益保护法》的保护对象为生活消费需要购买、使用商品或者接受服务的个人、单位或集体。', '对', '错', '', '', '', 'A', '消费者权益保护法');
INSERT INTO `pufa_import2` VALUES ('10', '10', '判断题', '2013年修正的《中华人民共和国消费者权益保护法》明确规定，网购商品一律实行7天无理由退货。', '对', '错', '', '', '', 'B', '消费者权益保护法');
INSERT INTO `pufa_import2` VALUES ('11', '11', '判断题', '保护消费者的合法权益是全社会的共同责任。', '对', '错', '', '', '', 'A', '消费者权益保护法');
INSERT INTO `pufa_import2` VALUES ('12', '12', '判断题', '有关行政部门应当听取消费者和消费者协会等组织对经营者交易行为、商品和服务质量问题的意见，及时调查处理，保护消费者的合法权益。', '对', '错', '', '', '', 'A', '消费者权益保护法');
INSERT INTO `pufa_import2` VALUES ('13', '13', '判断题', '安全生产管理，坚持安全第一、预防为主的方针。', '对', '错', '', '', '', 'A', '安全生产法');
INSERT INTO `pufa_import2` VALUES ('14', '14', '判断题', '生产经营单位进行爆破、吊装等危险作业，应当安排专门人员进行现场安全管理,要确保操作规程的遵守和安全措施的落实。', '对', '错', '', '', '', 'A', '安全生产法');
INSERT INTO `pufa_import2` VALUES ('15', '15', '判断题', '生产经营单位发生生产安全事故造成人员伤亡、他人财产损失的，应当依法承担赔偿责任；拒不承担或者其负责人逃匿的，由公安部门依法强制执行。', '对', '错', '', '', '', 'B', '安全生产法');
INSERT INTO `pufa_import2` VALUES ('16', '16', '判断题', '生产经营单位的从业人员有依法获得安全生产保障的权利，并应当依法履行安全生产方面的义务。', '对', '错', '', '', '', 'A', '安全生产法');
INSERT INTO `pufa_import2` VALUES ('17', '17', '判断题', '生产经营单位必须为从业人员提供符合国家标准或者行业标准的劳动防护用品，并监督、教育从业人员按照使用规则佩戴、使用。', '对', '错', '', '', '', 'A', '安全生产法');
INSERT INTO `pufa_import2` VALUES ('18', '18', '判断题', '生产经营单位的法定代表人对本单位的安全生产工作全面负责。', '对', '错', '', '', '', 'B', '安全生产法');
INSERT INTO `pufa_import2` VALUES ('19', '19', '判断题', '危险物品的生产、储存单位以及矿山、金属冶炼单位应当有注册安全工程师从事安全生产管理工作。', '对', '错', '', '', '', 'A', '安全生产法');
INSERT INTO `pufa_import2` VALUES ('20', '20', '判断题', '各级人民政府及其有关部门应当采取多种形式，加强对有关安全生产的法律、法规和安全生产知识的宣传，增强全社会的安全生产意识。', '对', '错', '', '', '', 'A', '安全生产法');
INSERT INTO `pufa_import2` VALUES ('21', '21', '判断题', '生产经营单位应当具备的安全生产条件所必需的资金投入，由生产经营单位的决策机构，主要负责人或者个人经营的投资人予以保证，并对由于安全生产所必需的资金投入不足导致的后果承担责任。', '对', '错', '', '', '', 'A', '安全生产法');
INSERT INTO `pufa_import2` VALUES ('22', '22', '判断题', '生产经营单位生产、经营、运输、储存、使用危险物品或者处置废弃危险物品，必须执行有关法律、法规和国家标准或者行业标准，建立专门的安全管理制度，采取可靠的安全措施，接受有关主管部门依法实施的监督管理。', '对', '错', '', '', '', 'A', '安全生产法');
INSERT INTO `pufa_import2` VALUES ('23', '23', '判断题', '两个以上生产经营单位在同一作业区域内进行生产经营活动，可能危及对方生产安全的，应当签订安全生产管理协议，明确各自的安全生产管理职责和应当采取的安全措施，并指定专职安全生产管理人员进行安全检查与协调。', '对', '错', '', '', '', 'A', '安全生产法');
INSERT INTO `pufa_import2` VALUES ('24', '24', '判断题', '生产经营单位应当建立健全生产安全事故隐患排查治理制度，采取技术、管理措施，及时发现并消除事故隐患。事故隐患排查治理情况应当如实记录，并向从业人员通报。', '对', '错', '', '', '', 'A', '安全生产法');
INSERT INTO `pufa_import2` VALUES ('25', '25', '判断题', '党和国家工作人员或者其他从事公务的人员中的共产党员，利用职务上的便利，为他人谋利益，接受其邀请，本人或携带亲友外出旅游，费用由邀请方支付的行为，以贪污论。', '对', '错', '', '', '', 'B', '德廉');
