INSERT INTO `pufa_import2` VALUES ('35', '35', '判断题', '《中国共产党纪律处分条例》规定，利用职务上的便利，将本人或者亲属应当由个人支付的费用，由下属单位或者其他单位支付、报销的行为属贪污行为。', '对', '错', '', '', '', 'B', '德廉');
INSERT INTO `pufa_import2` VALUES ('36', '36', '判断题', '《中国共产党纪律处分条例》规定，党和国家机关违反有关规定，在对内对外活动中将接受的礼品集体私分的，以私分国有资产论，根据个人所得数额和所起作用，以违反财经纪律行为处理。', '对', '错', '', '', '', 'B', '德廉');
