INSERT INTO `pufa_import2` VALUES ('39', '39', '判断题', '选任制公务员在选举结果生效时即任当选职务；任期届满不再连任，或者任期内辞职、被罢免、被撤职的，其所任职务即终止。', '对', '错', '', '', '', 'A', '德廉');
INSERT INTO `pufa_import2` VALUES ('40', '40', '判断题', '公务员因工作需要在机关外兼职，应当经有关机关批准，可以领取兼职报酬。', '对', '错', '', '', '', 'B', '德廉');
INSERT INTO `pufa_import2` VALUES ('41', '41', '判断题', '厅局级正职以下领导职务或者副调研员以上及其他相当职务层次的非领导职务出现空缺，可以面向社会公开选拔，产生任职人选。', '对', '错', '', '', '', 'B', '德廉');
