INSERT INTO `pufa_import2` VALUES ('48', '48', '判断题', '行政机关公务员违法违纪，已经被立案调查，不宜继续履行职责的，任免机关可以决定暂停其履行职务。 ', '对', '错', '', '', '', 'A', '德廉');
