INSERT INTO `pufa_import2` VALUES ('51', '51', '判断题', '《公务员法》规定，定期考核的结果作为调整公务员职务、级别、工资以及公务员奖励、培训、辞退的依据。', '对', '错', '', '', '', 'A', '德廉');
