INSERT INTO `pufa_import2` VALUES ('55', '55', '判断题', '2014年8月31日第十二届全国人民代表大会常务委员会第十次会议通过：从2017年开始，香港特别行政区行政长官选举可以实行由等额选举产生的办法。', '对', '错', '', '', '', 'B', '时政');
INSERT INTO `pufa_import2` VALUES ('56', '56', '判断题', '社会主义法治理念具有丰富而深刻的内涵，其中属于社会主义法治本质要求的是执法为民。', '对', '错', '', '', '', 'A', '时政');
