INSERT INTO `pufa_import2` VALUES ('58', '58', '判断题', '习主席指出，要把维护社会大局稳定作为基本任务，把促进社会公平正义作为核心价值追求，把保障人民安居乐业作为根本目标。', '对', '错', '', '', '', 'A', '时政');
