INSERT INTO `pufa_import2` VALUES ('121', '121', '判断题', '公民、法人或者其他组织对某一具体行政行为在法定期限内，不提起行政诉讼又不履行的，行政机关可以申请法院强制执行。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('122', '122', '判断题', '行政机关工作人员作出的具体行政行为侵犯公民的合法权益造成损害的，由该工作人员所在的行政机关负责赔偿。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('123', '123', '判断题', '两个以上行政机关作出同一具体行政行为的，共同作出具体行政行为的行政机关是共同被告。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('124', '124', '判断题', '人民法院审理行政案件，对滥用职权作出的具体行政行为，可以判决被告重新作出具体行政行为。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('125', '125', '判断题', '人民法院审理行政案件，对具体行政行为是否合法进行审查。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('126', '126', '判断题', '人民法院判决被告重新作出具体行政行为的，被告可以以同一事实和理由作出与原具体行政行为基本相同的具体行政行为。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('127', '127', '判断题', '《行政诉讼法》规定，被告无正当理由拒不到庭，或者未经法庭许可中途退庭的，可以缺席判决。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('128', '128', '判断题', '行政案件由最初作出具体行政行为的行政机关所在地的人民法院管辖。经复议的案件，复议机关改变原具体行政行为的，应当由复议机关所在地法院管辖。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('129', '129', '判断题', '行政诉讼中，对作出的具体行政行为负有举证责任的是人民法院。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('130', '130', '判断题', '根据《行政诉讼法》的规定，公民对于行政机关的行政不作为，有权提起行政诉讼。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('131', '131', '判断题', '根据《行政诉讼法》的规定，公民、法人或者其他组织对于外交行为、国防行为提起的诉讼，人民法院不予受理。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('132', '132', '判断题', '复议机关在法定期间内不作行政复议决定的，当事人对复议机关的不作为不服提起诉讼的，应以复议机关为被告。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('133', '133', '判断题', '在行政诉讼中，被告不得自行向原告和证人收集证据。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('134', '134', '判断题', '《行政诉讼法》规定，当事人对已经发生法律效力的判决或者裁定提出申诉后，原判决或者裁定应当暂停执行。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('135', '135', '判断题', '经行政复议的案件，复议机关改变原具体行政行为的，也可以由由复议机关所在地人民法院管辖。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('136', '136', '判断题', '在行政诉讼中，当事人不服人民法院第一审判决的，有权在判决书送达之日起15日内向上一级人民法院提出上诉。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('137', '137', '判断题', '行政许可直接涉及申请人与他人之间重大利益关系的，行政机关在作出行政许可决定前，应当告知申请人、利害关系人享有要求听证的权利；申请人、利害关系人在被告知听证权利之日起5日内提出听证申请的，行政机关应当在20日内组织听证。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('138', '138', '判断题', '《行政许可法》适用于有关行政机关对其他行政机关或者对其直接管理的事业单位的人事、财务、外事等事项的审批。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('139', '139', '判断题', '行政机关实施行政许可，依照法律、行政法规收取费用的，应当按照公布的法定项目和标准收费；所收取的费用必须全部上缴国库，任何机关或者个人不得以任何形式截留、挪用、私分或者变相私分。财政部门不得以任何形式向行政机关返还或者变相返还实施行政许可所收取的费用。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('140', '140', '判断题', '被许可人以欺骗、贿赂等不正当手段取得行政许可的，可以予以撤销。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('141', '141', '判断题', '《政府采购法》规定，在政府采购活动中，采购人员及相关人员与供应商有利害关系的，应当回避。供应商认为采购人员及相关人员与其他供应商有利害关系的，可以申请其回避。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('142', '142', '判断题', '《行政诉讼法》规定，因不动产提起的行政诉讼，由原告或被告住所地人民法院管辖。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('143', '143', '判断题', '《行政诉讼法》规定，行政诉讼中两个以上人民法院都有管辖权的案件，原告可以选择其中一个人民法院提起诉讼。原告向两个以上有管辖权的人民法院提起诉讼的，由最先立案的人民法管辖。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('144', '144', '判断题', '《行政诉讼法》规定，因不动产提起诉讼的案件从行政行为作出之日起超过二十年，其他案件从行政行为作出之日起超过五年提起诉讼的，人民法院不予受理。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('145', '145', '判断题', '有权申请行政复议的公民死亡的，其他人无权代为申请行政复议。有权申请行政复议的公民为无民事行为能力人或者限制民事行为能力人的，其法定代理人可以代为申请行政复议。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('146', '146', '判断题', '对地方各级人民政府的具体行政行为不服的，可以向上一级地方人民政府申请行政复议。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('147', '147', '判断题', '在行政复议过程中，被申请人可以自行向申请人和其他有关组织或者个人收集证据。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('148', '148', '判断题', '行政复议申请人应当以书面形式申请行政复议，不能以口头形式申请。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('149', '149', '判断题', '情况复杂，不能在规定期限内作出行政复议决定的，经行政复议机关负责人批准，可以适当延长行政复议期限，但延长期限最多不超过30日。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('150', '150', '判断题', '公民认为行政机关不依法履行法定职责的，不能申请行政复议。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('151', '151', '判断题', '公务员不服人事处理决定的，不能申请行政复议。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('152', '152', '判断题', '公民、法人或者其他组织向人民法院提起行政诉讼，人民法院已经依法受理的，不得申请行政复议。。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('153', '153', '判断题', '行政复议机关应当审查申请行政复议的具体行政行为的合法性与适当性。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('154', '154', '判断题', '行政强制措施由法律、法规规定的行政机关在法定职权范围内实施。行政强制措施权不得委托。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('155', '155', '判断题', '被申请人不提交当初作出具体行政行为的证件，依据和其他有关材料的，行政复议机关可以撤销原具体行政行为。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('156', '156', '判断题', '不服行政机关对民事纠纷作出的调解的，可以申请行政复议。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('157', '157', '判断题', '发展社会主义民主政治，最根本的是坚持党的领导、人民当家作主和依法治国的有机统一。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('158', '158', '判断题', '行政机关作出强制执行决定前，应当事先催告当事人履行义务。催告应当以书面形式作出。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('159', '159', '判断题', '《行政强制法》规定，划拨存款、汇款应当由法律规定的行政机关决定，并书面通知金融机构。金融机构接到行政机关依法作出划拨存款、汇款的决定后，应当立即划拨。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('160', '160', '判断题', '在行政诉讼中，人民法院如果发现行政法规和行政规章与宪法、法律相抵触，可以撤销。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('161', '161', '判断题', '行政机关应当于举行听证的七日前将举行听证的时间、地点通知申请人、利害关系人，必要时予以公告。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('162', '162', '判断题', '依法拍卖财物，由行政机关委托拍卖机构依照《中华人民共和国拍卖法》的规定办理。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('163', '163', '判断题', '具体行政行为认定事实不清的，证据不足的，行政复议机关可以调查补正后作出维持决定。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('164', '164', '判断题', '行政机关工作人员办理行政许可、实施监督检查，索取或者收受他人财物或者谋取其他利益，构成犯罪的，依法追究刑事责任。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('165', '165', '判断题', '行政机关依法作出不予行政许可的书面决定的，可以不说明理由，但要告知申请人享有依法申请行政复议或者提起行政诉讼的权利。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('166', '166', '判断题', '行政机关在调查或者进行检查时，执法人员不得少于两人，并应当向当事人或者有关人员出示证件。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('167', '167', '判断题', '行政复议机关受理行政复议申请，可以向申请人收取一定数额的行政复议费用。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('168', '168', '判断题', '在行政诉讼中，作为被告的行政机关法定代表人可以出庭参加诉讼，也可以委托代理人参加诉讼。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('169', '169', '判断题', '行政诉讼中的第三人只能通过申请参加到行政诉讼中来。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('170', '170', '判断题', '作出罚款决定的行政机关应当与收缴罚款的机构分离。作出行政处罚决定的行政机关及其执法人员在任何情况下均不得自行收缴罚款。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('171', '171', '判断题', '限制人身自由的行政处罚权即行政拘留的决定权只能由公安机关行使。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('172', '172', '判断题', '国家为了公共利益的需要，可以依照法律规定对公民的私有财产实行征收或者征用并给予补偿。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('173', '173', '判断题', '行政机关申请人民法院强制执行，不缴纳申请费。强制执行的费用由被执行人承担。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('174', '174', '判断题', '行政机关依申请提供政府信息，除可以收取检索、复制、邮寄等成本费用外，不得收取其他费用。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('175', '175', '判断题', '行政诉讼法规定，当事人对已发生法律效力的判决、裁定，认为确有错误的，可以向原审人民法院或者上一级人民法院提出申诉，判决、裁定停止执行。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('176', '176', '判断题', '对县级以上地方各级人民政府工作部门的具体行政行为不服的，由申请人选择，可以向该部门的本级人民政府申请行政复议，也可以向上一级主管部门申请行政复议。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('177', '177', '判断题', '被告行政机关委托的律师可以向原告和证人收集证据。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('178', '178', '判断题', '公民、法人或者其他组织认为行政机关和行政机关工作人员的具体行政行为侵犯其合法权益，有权依照《行政诉讼法》向人民法院提起诉讼。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('179', '179', '判断题', '行政复议既审查行政行为的合法性，又审查行政行为的合理性。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('180', '180', '判断题', '法律、行政法规设定的行政许可，其适用范围没有地域限制的，申请人取得的行政许可在全国范围内有效。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('181', '181', '判断题', '行政诉讼被告负举证责任，但不排除对某些事项应当由原告提供证据。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('182', '182', '判断题', '行政处罚由被处罚人所在地的县级以上地方人民政府具有行政处罚权的行政机关管辖。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('183', '183', '判断题', '凡是侵犯公民、法人和其他组织合法权益的具体行政行为，均属于行政复议的受理范围。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('184', '184', '判断题', '行政相对人对规章以下的规定不服，可就该规定单独申请行政复议。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('185', '185', '判断题', '行政机关实施行政许可，不得收取任何费用，但行政机关提供行政许可申请书格式文本的除外。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('186', '186', '判断题', '行政行为的撤销是因行政机关的过错引起，并给行政相对人造成损害的，则行政机关应负赔偿责任。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('187', '187', '判断题', '行政诉讼不适用调解是指对诉讼中任何问题均不得进行调解。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('188', '188', '判断题', '谁主张、谁举证是举证责任分配的一般原则，行政诉讼也不例外。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('189', '189', '判断题', '行政机关发现自己作出的行政行为有错可以依法自行撤销或改变行政行为，但有些具体行政行为的撤销或变更给相对人造成损失的，应当依法赔偿。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('190', '190', '判断题', '罚款主要适用于以牟取非法经济利益为目的的行政违法行为。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('191', '191', '判断题', '在行政复议期间，申请人认为需要停止执行的具体行政行为可以停止执行。', '对', '错', '', '', '', 'B', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('192', '192', '判断题', '公安干警在下班期间制止犯罪的行为是公务行为。', '对', '错', '', '', '', 'A', '行政法律法规');
INSERT INTO `pufa_import2` VALUES ('193', '193', '判断题', '内部行政行为可以适用行政申诉，但不得提起行政诉讼。', '对', '错', '', '', '', 'A', '行政法律法规');
