INSERT INTO `pufa_import2` VALUES ('203', '203', '判断题', '未依法说明不受理行政许可申请或者不予行政许可的理由的， 由其上级行政机关或者监察机关责令改正；情节严重的，对直接负责的主管人员和其他直接责任人员依法给予行政处分。', '对', '错', '', '', '', 'A', '行政法律法规');
