INSERT INTO `pufa_import2` VALUES ('247', '247', '判断题', '公民和组织因协助人民警察执行职务，造成人身伤亡或者财产损失的，应当按照国家有关规定给予抚恤或者补偿。', '对', '错', '', '', '', 'A', '人民警察法');
