INSERT INTO `pufa_import2` VALUES ('263', '263', '判断题', '根据《就业促进法》规定，职业中介机构根据需要可以扣押劳动者的居民身份证和其他证件，或者向劳动者收取押金。', '对', '错', '', '', '', 'B', '劳动法律');
INSERT INTO `pufa_import2` VALUES ('264', '264', '判断题', '我国规定领取失业保险金的期限最多不超过12个月。', '对', '错', '', '', '', 'B', '劳动法律');
INSERT INTO `pufa_import2` VALUES ('265', '265', '判断题', '公共就业服务机构应当不断提高服务的质量和效率，不得从事经营性活动。公共就业服务经费纳入同级财政预算。', '对', '错', '', '', '', 'A', '劳动法律');
INSERT INTO `pufa_import2` VALUES ('266', '266', '判断题', '当事人申请劳动争议调解应当书面申请，不得口头申请。', '对', '错', '', '', '', 'B', '劳动法律');
INSERT INTO `pufa_import2` VALUES ('267', '267', '判断题', '职工在上下班途中，受到意外伤害的应当认定为工伤。', '对', '错', '', '', '', 'B', '劳动法律');
INSERT INTO `pufa_import2` VALUES ('268', '268', '判断题', '劳动者提前30日以书面形式通知用人单位的，可以解除劳动合同。', '对', '错', '', '', '', 'A', '劳动法律');
INSERT INTO `pufa_import2` VALUES ('269', '269', '判断题', '劳动者可拒绝违章指挥和强令进行没有职业病防护措施的作业。', '对', '错', '', '', '', 'A', '劳动法律');
INSERT INTO `pufa_import2` VALUES ('270', '270', '判断题', '中华人民共和国民法调整公民之间、法人之间、公民与法人之间的财产关系和人身关系。', '对', '错', '', '', '', 'B', '民法');
INSERT INTO `pufa_import2` VALUES ('271', '271', '判断题', '民事法律关系的主体是指参加民事法律关系，享有民事权利，承担民事义务的特定的当事人。', '对', '错', '', '', '', 'B', '民法');
INSERT INTO `pufa_import2` VALUES ('272', '272', '判断题', '监护人不履行监护职责或者侵害被监护人的合法利益的，应当承担责任；给被监护人造成财产损失的，应当赔偿损失。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('273', '273', '判断题', '法人或者其他组织的法定代表人、负责人超越权限的行为，除相对人知道或者应当知道其超越权限的以外，该代表行为有效。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('274', '274', '判断题', '李某被宣告死亡后又重新出现，经申请法院撤销了对他的死亡宣告，其配偶再婚后配偶又死亡的，李某与原配偶之间的夫妻关系自行恢复。', '对', '错', '', '', '', 'B', '民法');
INSERT INTO `pufa_import2` VALUES ('275', '275', '判断题', '诉讼时效期间届满，权利人仍不行使其民事权利的，其民事权利即归于消灭。', '对', '错', '', '', '', 'B', '民法');
INSERT INTO `pufa_import2` VALUES ('276', '276', '判断题', '共有人对共有的不动产或者动产，没有约定为按份共有或者共同共有，或者约定不明确的，除共有人具有家庭关系等外，视为按份共有。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('277', '277', '判断题', '抵押权人和抵押人在订立抵押合同时，可以约定在债务履行期届满抵押权人未受清偿时，抵押物的所有权转移为债权人所有。', '对', '错', '', '', '', 'B', '民法');
INSERT INTO `pufa_import2` VALUES ('278', '278', '判断题', '人身权是和生命联系在一起的，法人是组织体，谈不上什么人身。因此，没有人身权。', '对', '错', '', '', '', 'B', '民法');
INSERT INTO `pufa_import2` VALUES ('279', '279', '判断题', '当事人订立合同，应当具有相应的民事权利能力和民事行为能力。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('280', '280', '判断题', '无处分权的人处分他人财产，经权利人追认或者无处分权的人订立合同后取得处分权的，该合同有效。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('281', '281', '判断题', '当事人约定由债务人向第三人履行债务的，债务人未向第三人履行债务或者履行债务不符合约定的，应当向第三人承担违约责任。', '对', '错', '', '', '', 'B', '民法');
INSERT INTO `pufa_import2` VALUES ('282', '282', '判断题', '公民的合法财产受法律保护，禁止任何组织或者个人侵占、哄抢、破坏或者非法查封、扣押、冻结、没收。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('283', '283', '判断题', '债权人转让债权应当经债务人同意，未经债务人同意，该转让对债务人不发生效力。', '对', '错', '', '', '', 'B', '民法');
INSERT INTO `pufa_import2` VALUES ('284', '284', '判断题', '当事人一方明确表示或者以自己的行为表明不履行合同义务的，对方可以在履行期限届满之前要求其承担违约责任。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('285', '285', '判断题', '当事人既约定违约金，又约定定金的，一方违约时，对方可以选择使用违约金或者定金条款。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('286', '286', '判断题', '标的物的所有权自合同生效时起转移，但法律另有规定或当事人另有约定的除外。', '对', '错', '', '', '', 'B', '民法');
INSERT INTO `pufa_import2` VALUES ('287', '287', '判断题', '出卖人出卖交承运人运输的在途标的物，除当事人另有约定的以外，毁损、灭失的风险自合同成立时起由买受人承担。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('288', '288', '判断题', '适用特别程序审理选民资格案件，应当由对选民资格有争议的公民住所地法院管辖。', '对', '错', '', '', '', 'B', '民法');
INSERT INTO `pufa_import2` VALUES ('289', '289', '判断题', '当事人对已经发生法律效力的调解书，提出证据证明调解违反自愿原则或者调解协议的内容违反法律的，可以申请再审。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('290', '290', '判断题', '根据民事诉讼法的规定，当事人对自己提出的主张，有责任提供证据。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('291', '291', '判断题', '人民法院财产保全的方法有查封、扣押、冻结、罚款或者法律规定的其他方法。', '对', '错', '', '', '', 'B', '民法');
INSERT INTO `pufa_import2` VALUES ('292', '292', '判断题', '公证机关制作的依法赋予强制执行力的债权文书，当事人可以向有管辖权的法院申请强制执行。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('293', '293', '判断题', '人民法院对于追索赡养费、抚养费、抚恤金、医疗费用、劳动报酬的民事诉讼案件，应当裁定先予执行。', '对', '错', '', '', '', 'B', '民法');
INSERT INTO `pufa_import2` VALUES ('294', '294', '判断题', '发生法律效力的民事判决，当事人必须履行。一方拒绝履行的，对方当事人可以向法院申请执行，申请执行的期间为2年。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('295', '295', '判断题', '对公民提起的民事诉讼，由被告住所地法院管辖；被告住所地与经常居住地不一致的，由经常居住地法院管辖。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('296', '296', '判断题', '因不动产纠纷提起的民事诉讼，由被告住所地或者不动产所在地法院管辖。', '对', '错', '', '', '', 'B', '民法');
INSERT INTO `pufa_import2` VALUES ('297', '297', '判断题', '在民事诉讼中，当事人因不可抗拒的事由耽误期限的，可以在障碍消除后10日内，申请顺延期限，是否准许，由法院裁定。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('298', '298', '判断题', '未经人民法院依法判决，对任何人不得确定有罪。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('299', '299', '判断题', '对于证据不足，不能认定被告人有罪的案件，人民法院应当作出证据不足、指控的犯罪不能成立的判决。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('300', '300', '判断题', '人民法院审理案件时，认为地方行政规章与国务院部、委规章不一致的，参照国务院部、委规章。', '对', '错', '', '', '', 'B', '民法');
INSERT INTO `pufa_import2` VALUES ('301', '301', '判断题', '生理上、精神上有缺陷或者年幼，不能辨别是非、不能正确表达的人，不能作证人。', '对', '错', '', '', '', '对 ', '民法');
INSERT INTO `pufa_import2` VALUES ('302', '302', '判断题', '合同无效、被撤销或者终止的，不影响合同中独立存在的有关解决争议方法的条款的效力。', '对', '错', '', '', '', '对 ', '民法');
INSERT INTO `pufa_import2` VALUES ('303', '303', '判断题', '当事人一方因不可抗力不能履行合同的，应当及时通知对方，以减轻可能给对方造成的损失，并应当在合理期限内提供证明。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('304', '304', '判断题', '法律服务的指导、管理职能主要由人民法院承担。', '对', '错', '', '', '', 'B', '民法');
INSERT INTO `pufa_import2` VALUES ('305', '305', '判断题', '民事法律行为当事人的意思表示只能采取明示的形式，不能采取默示的形式。', '对', '错', '', '', '', 'B', '民法');
INSERT INTO `pufa_import2` VALUES ('306', '306', '判断题', '李某建房挖地基时挖出三件古代陶器，经有关部门鉴定具有很高的价值，这三件陶器归李某所有。', '对', '错', '', '', '', 'B', '民法');
INSERT INTO `pufa_import2` VALUES ('307', '307', '判断题', '在我国律师见证一般是人民法院可以直接采信的证据。', '对', '错', '', '', '', 'B', '民法');
INSERT INTO `pufa_import2` VALUES ('308', '308', '判断题', '公民以他的户籍所在地的居住地为住所，经常居住地与住所不一致的，户籍所在地的居住地视为住所。', '对', '错', '', '', '', 'B', '民法');
INSERT INTO `pufa_import2` VALUES ('309', '309', '判断题', '公民因意外事故下落不明，从事故发生之日起满二年的，利害关系人可以向人民法院申请宣告他死亡。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('310', '310', '判断题', '甲、乙、丙三人合伙经营，后甲因急事用钱，要将自己在合伙中的份额转让，乙和第三人丁均欲以同一价格购买，甲应该卖给乙。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('311', '311', '判断题', '英国驻华领事上街购物乘坐的汽车被我国公民王某撞坏，该领事即向当地人民法院起诉，请求王某赔偿损失。本案适用中国法律。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('312', '312', '判断题', '普通合伙人对合伙的债务承担有限责任，公司股东对公司债务承担有限责任。', '对', '错', '', '', '', 'B', '民法');
INSERT INTO `pufa_import2` VALUES ('313', '313', '判断题', '在诉讼时效期间的最后6个月内内，因不可抗力或者其他障碍不能行使请求权的，诉讼时效中止。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('314', '314', '判断题', '公民享有肖像权，未经本人同意，不得以营利为目的使用公民的肖像。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('315', '315', '判断题', '某报社在一篇新闻报道中，披露未成年人甲是乙的私生子，致使甲备受同学的嘲笑与奚落，给甲的生活和学习造成重大影响，甲因精神痛苦自残左手无名指。报社的行为侵害了甲的隐私权。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('316', '316', '判断题', '依法成立的合同，对当事人具有法律约束力。当事人应当按照约定履行自己的义务，不得擅自变更或者解除合同。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('317', '317', '判断题', '对格式条款的理解发生争议的，应当按照通常理解予以解释。对格式条款有两种以上解释的，应当作出不利于提供格式条款一方解释。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('318', '318', '判断题', '买卖合同中，标的物的所有权自标的物交付时起转移，但法律另有规定或者当事人另有约定的除外。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('319', '319', '判断题', '董某在银行取款时，意外发现自己的银行卡里多了10万元存款，经查，是银行误将一个同名同姓的人的存款打入董某的卡中。此情况下可以构成不当得利。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('320', '320', '判断题', '销售者不能指明缺陷产品的生产者也不能指明缺陷产品的供货者的，销售者应当承担赔偿责任。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('321', '321', '判断题', '杨某售给高某一台机器、价格1万元，高某付款时不慎多给了杨某1500元，杨某用这1500元购买了200棵果树苗，转手出售获利2000元，事隔不久，高某发觉向杨某付款时出了差错，遂要求杨某返还，此时杨某应返还高某1500元。', '对', '错', '', '', '', 'A', '民法');
INSERT INTO `pufa_import2` VALUES ('322', '322', '判断题', '甲将从上游水源流下的水截留在自己的土地，土地位于下游的乙要求甲停止截留。根据民法规定，甲乙之间的法律关系属于相邻关系 。', '对', '错', '', '', '', 'A', '物权法');
INSERT INTO `pufa_import2` VALUES ('323', '323', '判断题', '继承人虐待被继承人情节严重的，如以后确有悔改表现，而且被继承人生前又表示宽恕的，可不确认其丧失继承权。', '对', '错', '', '', '', 'A', '继承法');
INSERT INTO `pufa_import2` VALUES ('324', '324', '判断题', '遗产是指公民死亡时遗留下来的公民收入和储蓄。', '对', '错', '', '', '', 'B', '继承法');
INSERT INTO `pufa_import2` VALUES ('325', '325', '判断题', '继承开始后，继承人放弃继承的，应当在遗产处理前，做出放弃继承的表示。没有表示的，视为转继承。', '对', '错', '', '', '', 'B', '继承法');
INSERT INTO `pufa_import2` VALUES ('326', '326', '判断题', '相互有继承关系的几个人在同一事件中死亡，如不能确定死亡先后时间的，推定没有继承人的人先死亡。死亡人各自都有继承人的，如几个死亡人辈份不同，推定长辈先死亡。', '对', '错', '', '', '', 'A', '继承法');
INSERT INTO `pufa_import2` VALUES ('327', '327', '判断题', '被继承人的子女先于被继承人死亡的，由被继承人的子女的晚辈直系血亲继承其父母有权继承的遗产份额，这种继承制度被称为代位继承。', '对', '错', '', '', '', 'A', '继承法');
INSERT INTO `pufa_import2` VALUES ('328', '328', '判断题', '《继承法》规定，遗嘱应当对胎儿保留必要的遗产份额。', '对', '错', '', '', '', 'A', '继承法');
INSERT INTO `pufa_import2` VALUES ('329', '329', '判断题', '遗产分割时，应当保留胎儿的继承份额。胎儿出生时是死体的，保留的份额按照法定继承办理。', '对', '错', '', '', '', 'A', '继承法');
INSERT INTO `pufa_import2` VALUES ('330', '330', '判断题', '遗嘱人以不同形式立有数份内容相抵触的遗嘱，其中有公证遗嘱的，以最后书写遗嘱为准。', '对', '错', '', '', '', 'B', '继承法');
INSERT INTO `pufa_import2` VALUES ('331', '331', '判断题', '我国宪法规定，中华人民共和国公民享有休息的权利。', '对', '错', '', '', '', 'A', '宪法');
INSERT INTO `pufa_import2` VALUES ('332', '332', '判断题', '现行宪法规定，全国人大代表未经许可，享有不受逮捕和审判的权利。', '对', '错', '', '', '', 'B', '宪法');
INSERT INTO `pufa_import2` VALUES ('333', '333', '判断题', '民族自治地方的自治机关经全国人大批准，可以组织本地方维护社会治安的公安部队。', '对', '错', '', '', '', 'B', '宪法');
INSERT INTO `pufa_import2` VALUES ('334', '334', '判断题', '新中国历史上第一部正式宪法是《五四宪法》。', '对', '错', '', '', '', 'A', '宪法');
INSERT INTO `pufa_import2` VALUES ('335', '335', '判断题', '国务院是我国最高国家权力机关、最高国家行政机关和中央人民政府。', '对', '错', '', '', '', 'B', '宪法');
INSERT INTO `pufa_import2` VALUES ('336', '336', '判断题', '根据宪法，我国乡、民族乡、镇的人民代表大会每届任期为三年。', '对', '错', '', '', '', 'B', '宪法');
INSERT INTO `pufa_import2` VALUES ('337', '337', '判断题', '因国家安全或者追查刑事犯罪的需要，公安机关、检察机关可以依法对公民的通信进行检查。', '对', '错', '', '', '', 'A', '宪法');
INSERT INTO `pufa_import2` VALUES ('338', '338', '判断题', '农村的宅基地、自留地和自留山归公民个人所有。', '对', '错', '', '', '', 'B', '宪法');
INSERT INTO `pufa_import2` VALUES ('339', '339', '判断题', '我国公民的平等权既包括立法上的平等权，也包括司法、守法上的平等权。', '对', '错', '', '', '', 'B', '宪法');
INSERT INTO `pufa_import2` VALUES ('340', '340', '判断题', '我国宪法规定，审计机关在国务院总理的领导下，依照法律的规定独立行使审计监督权。', '对', '错', '', '', '', 'A', '宪法');
INSERT INTO `pufa_import2` VALUES ('341', '341', '判断题', '《中国人民政治协商会议章程》规定：中国人民政治协商会议全国委员会对地方委员会的关系和地方委员会对下一级地方委员会的关系是监督关系。\n。', '对', '错', '', '', '', 'B', '宪法');
INSERT INTO `pufa_import2` VALUES ('342', '342', '判断题', '我国公民享有言论、出版、集会、游行、示威、罢工的权利。', '对', '错', '', '', '', 'B', '宪法');
INSERT INTO `pufa_import2` VALUES ('343', '343', '判断题', '矿藏、水流、森林、山岭、草原、荒地、滩涂等自然资源，都可以属于集体所有。', '对', '错', '', '', '', 'B', '宪法');
INSERT INTO `pufa_import2` VALUES ('344', '344', '判断题', '民族区域自治地方的自治机关是自治区、自治州、自治县的人民代表大会、人民政府、人民法院和人民检察院。', '对', '错', '', '', '', 'B', '宪法');
INSERT INTO `pufa_import2` VALUES ('345', '345', '判断题', '小张同学已经年满十八周岁，他有权作为选民去参加青岛市的市人民代表大会代表的选举投票。', '对', '错', '', '', '', 'B', '宪法');
INSERT INTO `pufa_import2` VALUES ('346', '346', '判断题', '全国人大常委会可以制定刑事、民事、国家机构和其他事项的基本法律。', '对', '错', '', '', '', 'B', '宪法');
INSERT INTO `pufa_import2` VALUES ('347', '347', '判断题', '我国《宪法》规定，任何公民，非经人民检察院批准或者决定或者人民法院决定，并由公安机关执行，不受逮捕。', '对', '错', '', '', '', 'A', '宪法');
INSERT INTO `pufa_import2` VALUES ('348', '348', '判断题', '宪法和法律是我们立党、治党、管党的总章程，是全党最基本、最重要、最全面的行为规范。', '对', '错', '', '', '', 'B', '宪法');
INSERT INTO `pufa_import2` VALUES ('349', '349', '判断题', '宪法是国家的根本法，是治国安邦的总章程，具有最高的法律地位、法律权威、法律效力。', '对', '错', '', '', '', 'A', '宪法');
INSERT INTO `pufa_import2` VALUES ('350', '350', '判断题', '全国人民代表大会代表在全国人民代表大会各种会议上的发言和表决，不受法律追究。', '对', '错', '', '', '', 'A', '宪法');
INSERT INTO `pufa_import2` VALUES ('351', '351', '判断题', '地方各级人民代表大会每届任期五年。', '对', '错', '', '', '', 'A', '宪法');
INSERT INTO `pufa_import2` VALUES ('352', '352', '判断题', '中华人民共和国公民对于任何国家机关和国家机关的工作人员，有提出批评和建议的权利。', '对', '错', '', '', '', 'A', '宪法');
INSERT INTO `pufa_import2` VALUES ('353', '353', '判断题', '我国的全国人民代表大会和地方各级人民代表大会都设有常务委员会。', '对', '错', '', '', '', 'B', '宪法');
INSERT INTO `pufa_import2` VALUES ('354', '354', '判断题', '通信自由属于公民的政治权利和自由。', '对', '错', '', '', '', 'B', '宪法');
INSERT INTO `pufa_import2` VALUES ('355', '355', '判断题', '我国基层政权机关与村民委员会、居民委员会的关系是领导与被领导关系。', '对', '错', '', '', '', 'B', '宪法');
INSERT INTO `pufa_import2` VALUES ('356', '356', '判断题', '任何组织或者个人都不得有超越宪法和法律的特权，一切违反宪法和法律的行为都必须予以追究。', '对', '错', '', '', '', 'A', '宪法');
INSERT INTO `pufa_import2` VALUES ('357', '357', '判断题', '我国根本政治制度是人民代表大会制度。', '对', '错', '', '', '', 'A', '宪法');
INSERT INTO `pufa_import2` VALUES ('358', '358', '判断题', '全国人民代表大会修改宪法、解释宪法、监督宪法的实施。', '对', '错', '', '', '', 'B', '宪法');
INSERT INTO `pufa_import2` VALUES ('359', '359', '判断题', '省、自治区、直辖市政府决定乡、民族乡、镇的建置和区域划分。', '对', '错', '', '', '', 'A', '宪法');
INSERT INTO `pufa_import2` VALUES ('360', '360', '判断题', '根据《选举法》的规定，在选举日必须对代表候选人进行介绍。', '对', '错', '', '', '', 'B', '选举法');
INSERT INTO `pufa_import2` VALUES ('361', '361', '判断题', '每次选举所投的票数，多于投票人数的无效，等于或少于投票人数的有效。', '对', '错', '', '', '', 'A', '选举法');
INSERT INTO `pufa_import2` VALUES ('362', '362', '判断题', '青岛市李沧区人民代表大会代表是由选民间接选举产生的。', '对', '错', '', '', '', 'B', '选举法');
INSERT INTO `pufa_import2` VALUES ('363', '363', '判断题', '妇女享有与男子平等的选举权和被选举权。居民委员会、村民委员会成员中，妇女应当有适当的名额。', '对', '错', '', '', '', 'A', '选举法');
INSERT INTO `pufa_import2` VALUES ('364', '364', '判断题', '《立法法》规定，授权国务院可以根据实际需要，对其中的部分事项先制定行政法规，授权决定应当明确授权的目的、范围。', '对', '错', '', '', '', 'A', '立法法');
INSERT INTO `pufa_import2` VALUES ('365', '365', '判断题', '《立法法》规定，授权国务院制定行政法规的，授权的期限不得超过五年，但是授权决定另有规定的除外。', '对', '错', '', '', '', 'A', '立法法');
INSERT INTO `pufa_import2` VALUES ('366', '366', '判断题', '立法应当依照法定的权限和程序，从国家整体利益出发，维护社会主义法制的统一和尊严。', '对', '错', '', '', '', 'A', '立法法');
INSERT INTO `pufa_import2` VALUES ('367', '367', '判断题', '全国人民代表大会及其常务委员会可以根据改革发展的需要作出决定，就特定事项在部分地方暂停适用法律的部分规定。', '对', '错', '', '', '', 'A', '立法法');
INSERT INTO `pufa_import2` VALUES ('368', '368', '判断题', '立法应当从实际出发，适应经济社会发展要求，科学合理地规定公民、法人和其他组织的权利与义务、国家机关的权力与责任，做到法律规范明确、具体，具有可执行性和可操作性。', '对', '错', '', '', '', 'A', '立法法');
INSERT INTO `pufa_import2` VALUES ('369', '369', '判断题', '《立法法》规定，列入常务委员会会议议程的法律案，法律委员会、有关的专门委员会和常务委员会工作机构应当听取各方面的意见。听取意见可以采取座谈会、论证会、听证会等多种形式。', '对', '错', '', '', '', 'A', '立法法');
INSERT INTO `pufa_import2` VALUES ('370', '370', '判断题', '法律案有关问题具有较强的专业性或者需要进行可行性评价的，可以召开论证会，听取专家、有关部门等方面的意见。', '对', '错', '', '', '', 'B', '立法法');
INSERT INTO `pufa_import2` VALUES ('371', '371', '判断题', '法律案有关问题存在重大意见分歧或者涉及利益关系重大调整的，可以依申请召开听证会，听取利害关系人、有关部门、专家等方面的意见。', '对', '错', '', '', '', 'B', '立法法');
INSERT INTO `pufa_import2` VALUES ('372', '372', '判断题', '列入常务委员会会议议程的法律案，应当将法律草案文本及其说明等通过网络等媒介向社会公布，征求意见，但是经委员长会议决定不予公布的除外。', '对', '错', '', '', '', 'A', '立法法');
INSERT INTO `pufa_import2` VALUES ('373', '373', '判断题', '法律草案修改稿经常务委员会会议审议，经委员长会议决定，常务委员会会议可以对个别有重大意见分歧的条文先予表决。', '对', '错', '', '', '', 'A', '立法法');
INSERT INTO `pufa_import2` VALUES ('374', '374', '判断题', '全国人民代表大会常务委员会应当依照宪法和法律的规定，加强对立法工作的组织协调，充分发挥在立法中的主导作用。', '对', '错', '', '', '', 'B', '立法法');
INSERT INTO `pufa_import2` VALUES ('375', '375', '判断题', '全国人民代表大会常务委员会通过每届任期的立法规划、年度立法计划等形式，加强对立法工作的统筹安排。立法规划和年度立法计划由委员长会议通过并向社会公布。', '对', '错', '', '', '', 'A', '立法法');
INSERT INTO `pufa_import2` VALUES ('376', '376', '判断题', '全国人民代表大会负责编制立法规划和拟订年度立法计划，并协助全国人民代表大会常务委员会督促立法规划和年度立法计划的落实。', '对', '错', '', '', '', 'B', '立法法');
INSERT INTO `pufa_import2` VALUES ('377', '377', '判断题', '法律被废止的，除由其他法律规定废止该法律的以外，由国家主席签署主席令予以公告。', '对', '错', '', '', '', 'A', '立法法');
INSERT INTO `pufa_import2` VALUES ('378', '378', '判断题', '全国人民代表大会有关的专门委员会、常务委员会工作机构根据法律实施的情况，可以组织对有关法律开展立法后评估，并向常务委员会提交报告。', '对', '错', '', '', '', 'A', '立法法');
INSERT INTO `pufa_import2` VALUES ('379', '379', '判断题', '青岛市制定地方性法规限于城市建设、市容卫生、环境保护等城市管理方面的事项。', '对', '错', '', '', '', 'A', '立法法');
INSERT INTO `pufa_import2` VALUES ('380', '380', '判断题', '规定本行政区域特别重大或者社会普遍关注事项的地方性法规，应当由人民代表大会通过。', '对', '错', '', '', '', 'A', '立法法');
INSERT INTO `pufa_import2` VALUES ('381', '381', '判断题', '全国人大通过法律案，应由全体代表的三分之二赞同表决才能通过。', '对', '错', '', '', '', 'B', '立法法');
INSERT INTO `pufa_import2` VALUES ('382', '382', '判断题', '全国人民代表大会和全国人民代表大会常务委员会行使国家立法权。', '对', '错', '', '', '', 'A', '立法法');
INSERT INTO `pufa_import2` VALUES ('383', '383', '判断题', '民族区域自治地方的人大常委会可以制定自治条例和单行条例。', '对', '错', '', '', '', 'B', '立法法');
INSERT INTO `pufa_import2` VALUES ('384', '384', '判断题', '在国家尚未制定法律、行政法规的领域，地方性法规可以在法定权限内进行先行性立法。', '对', '错', '', '', '', 'A', '立法法');
INSERT INTO `pufa_import2` VALUES ('385', '385', '判断题', '罪刑法定原则是我国刑法的基本原则之一，其基本含义是：法律明文规定为犯罪行为的，依照法律定罪处罚，法律没有明文规定为犯罪行为的，不得定罪处罚。其著名的格言是“法无明文规定不为罪，法无明文规定不处罚”。', '对', '错', '', '', '', 'A', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('386', '386', '判断题', '根据我国《刑法》的规定，任何犯罪的成立都必须具备以下四个方面的要件：犯罪客体、犯罪客观方面、犯罪主体、犯罪主观方面。', '对', '错', '', '', '', 'A', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('387', '387', '判断题', '国家机关实施危害社会的行为，例如私分国有资产、私分罚没财物、受贿、向其他单位行贿等，即使具有严重的社会危害性，也不构成犯罪。', '对', '错', '', '', '', 'B', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('388', '388', '判断题', '对任何人犯罪，在适用法律上人人平等。不允许任何人有超越法律的特权。', '对', '错', '', '', '', 'A', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('389', '389', '判断题', '年满30岁的精神病人张某，先后采用暴力手段抢劫他人的手机三部，对张某应当以抢劫罪追究刑事责任。', '对', '错', '', '', '', 'B', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('390', '390', '判断题', '我国《刑法》中的正当防卫，既是法律赋予公民的一项权利，也是公民应尽的法律义务。', '对', '错', '', '', '', 'B', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('391', '391', '判断题', '所谓国家安全，即我国人民民主专政政权和社会主义制度不受侵犯，我国的国家主权和领土完整不受侵犯。', '对', '错', '', '', '', 'A', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('392', '392', '判断题', '我国《刑法》的紧急避险中关于避免本人危险的规定，可以适用于职业上、业务上负有特定责任的人。', '对', '错', '', '', '', 'B', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('393', '393', '判断题', '李某将孙某推入水中，意图将其淹死，但听到孙某可怜的呼喊声，又将孙某救起并脱下自己的毛衣毛裤给孙某穿。李某的行为属于犯罪中止。对李某应当免除刑罚处罚。', '对', '错', '', '', '', 'A', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('394', '394', '判断题', '我国《刑法》中的共同犯罪，是指二人以上同时实施犯罪。', '对', '错', '', '', '', 'B', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('395', '395', '判断题', '刑法第规定教唆不满十八周岁的人犯罪的,应当从重处罚。如果被教唆的人没有犯被教唆的罪,对于教唆犯,可以从轻或者减轻处罚。', '对', '错', '', '', '', 'A', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('396', '396', '判断题', '无期徒刑虽然没有刑期限制，但在具体执行中，并不一定将犯罪分子关押到死，而是充分给其悔过自新、重新回归社会的机会。', '对', '错', '', '', '', 'A', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('397', '397', '判断题', '根据我国《刑法》规定，被判处管制刑以及被判处拘役、有期徒刑而宣告缓刑的犯罪分子，由司法行政机关负责执行或者进行考察。', '对', '错', '', '', '', 'B', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('398', '398', '判断题', '我国《刑法》中的免除处罚，就是对行为人只定罪而不判刑。', '对', '错', '', '', '', 'A', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('399', '399', '判断题', '某局机关会计冯某因贪污公款被立案侦查，其间，冯某检举分管副局长受贿30万元的犯罪行为，并经查证属实。冯某的行为构成重大立功，可以减轻或者免除处罚。', '对', '错', '', '', '', 'A', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('400', '400', '判断题', '被宣告缓刑的犯罪分子，在缓刑考验期限内，如果出现法律规定应当撤销缓刑的事由，就应当收监执行原判刑罚或者数罪并罚。', '对', '错', '', '', '', 'A', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('401', '401', '判断题', '魏某财迷心窍，以扬言披露姜某隐私的要挟方法勒索了5000元钱。事后姜某告发了魏某，公安机关以涉嫌敲诈勒索罪对他立案侦查。正当魏某不知如何是好时，他的一位朋友说可以到外面避避风头，过几年就没事了。该朋友的说法是对的，因为我国《刑法》中有追诉时效（即追究犯罪分子刑事责任的有效期限）的规定。', '对', '错', '', '', '', 'B', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('402', '402', '判断题', '放火烧毁自己或家庭所有的房屋以及财物的，不可能构成放火罪。', '对', '错', '', '', '', 'B', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('403', '403', '判断题', '无论是从事交通运输的人员，还是非交通运输人员，都可以构成交通肇事罪。', '对', '错', '', '', '', 'A', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('404', '404', '判断题', '国家机关工作人员郑某于2000年与女青年戴某登记结婚。婚后，由于戴某生的是女儿，郑某开始嫌弃她们母女，在外面另寻新欢。2004年年初，郑某在宴会上结识女青年刘某，不久就同居生活，时间长达5年之久，其间，刘某为郑某生了个儿子。由于郑某与刘某并未登记结婚，只是形成了事实婚姻，而我国法律是不承认事实婚姻的，因此，郑某的行为不构成重婚罪。', '对', '错', '', '', '', 'B', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('405', '405', '判断题', '国家机关或者金融、电信、交通、教育、医疗等单位的工作人员，违反国家规定，将本单位在履行职责或者提供服务过程中获得的公民个人信息，出售或者非法提供给他人，即使情节严重的，也不构成犯罪。', '对', '错', '', '', '', 'B', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('406', '406', '判断题', '以暴力、威胁方法阻碍全国人民代表大会和地方各级人民代表大会代表依法执行代表职务的，处三年以下有期徒刑、拘役、管制或者罚金。', '对', '错', '', '', '', 'A', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('407', '407', '判断题', '人民检察院对侦查终结的案件进行审查后，应当根据案件情况，分别作出起诉、免于起诉或者不起诉的决定。', '对', '错', '', '', '', 'B', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('408', '408', '判断题', '公安机关、人民检察院、人民法院认为犯罪嫌疑人、被告人有犯罪事实并且需要追究刑事责任的，应当立案。', '对', '错', '', '', '', 'A', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('409', '409', '判断题', '第一审法院判决被告人无罪的，如果被告人在押，在判决生效后，应当立即释放。', '对', '错', '', '', '', 'A', '刑法刑诉');
INSERT INTO `pufa_import2` VALUES ('410', '410', '判断题', '犯罪嫌疑人在被侦查机关第一次讯问后，有权委托辩护人。', '对', '错', '', '', '', 'A', '刑法刑诉');
